import java.io.*;
import java.util.*;
import java.util.concurrent.*;

public class ExtCmd {
    private Map<ExtCmdCondition, String> conditionsWithDescriptions;
    private Set<ExtCmdEndedListener> extCmdEndedListenerSet;
    private OutputReceiver outReceiver;
    private OutputReceiver errReceiver;
    private ErrorReporter errLog;
    private long startTime;
    private String[] execCommand;
    private String execCommandStr;
    private boolean logExecution;

    public ExtCmd() {
        conditionsWithDescriptions = new HashMap<>();
        extCmdEndedListenerSet = new HashSet<>();
        outReceiver = new OutputReceiver();
        errReceiver = new OutputReceiver();
        errLog = new ErrorReporter();
        startTime = 0;
        execCommand = new String[0];
        execCommandStr = "";
        logExecution = false;
    }

    public OutputStream exec(ProcessBuilder.Redirect redirect, File dir, String... args) throws IOException {
        execCommand = new String[args.length + 1];
        execCommand[0] = args[0];
        System.arraycopy(args, 1, execCommand, 1, args.length - 1);
        execCommandStr = Arrays.toString(execCommand);
        ProcessBuilder pb = new ProcessBuilder(execCommand);
        if (redirect != null) {
            pb.redirectInput(redirect);
        }
        if (dir != null) {
            pb.directory(dir);
        }
        Process p = pb.start();
        outReceiver.setProcess(p);
        errReceiver.setProcess(p);
        return p.getOutputStream();
    }

    public OutputData syncProcess() throws InterruptedException, IOException {
        Process p = outReceiver.getProcess();
        if (p == null) {
            throw new IllegalStateException("No process to wait for");
        }
        if (logExecution) {
            errLog.log("Starting command: " + execCommandStr);
        }
        startTime = System.currentTimeMillis();
        int exitCode = p.waitFor();
        long endTime = System.currentTimeMillis();
        if (logExecution) {
            errLog.log("Command finished with exit code: " + exitCode);
        }
        for (ExtCmdEndedListener listener : extCmdEndedListenerSet) {
            listener.onExtCmdEnded(this);
        }
        return new OutputData(outReceiver.getOutput(), errReceiver.getOutput(), exitCode, endTime - startTime);
    }

    public void checkForConditions() {
        for (Map.Entry<ExtCmdCondition, String> entry : conditionsWithDescriptions.entrySet()) {
            if (!entry.getKey().isMet()) {
                throw new IllegalStateException("Condition not met: " + entry.getValue());
            }
        }
    }

    public void addCondition(ExtCmdCondition condition, String description) {
        conditionsWithDescriptions.put(condition, description);
    }

    public void addExtCmdEndedListener(ExtCmdEndedListener listener) {
        extCmdEndedListenerSet.add(listener);
    }

    public void setOutReceiver(OutputReceiver outReceiver) {
        this.outReceiver = outReceiver;
    }

    public void setErrReceiver(OutputReceiver errReceiver) {
        this.errReceiver = errReceiver;
    }

    public void setErrorReporter(ErrorReporter errLog) {
        this.errLog = errLog;
    }

    public void setLogExecution(boolean logExecution) {
        this.logExecution = logExecution;
    }

    public interface OutputData {
        String getStdout();
        String getStderr();
        int getExitCode();
        long getExecutionTime();
    }

    public interface ExtCmdCondition {
        boolean isMet();
    }

    public interface ExtCmdEndedListener {
        void onExtCmdEnded(ExtCmd cmd);
    }
}
