import io.lettuce.core.RedisClient;
import io.lettuce.core.api.StatefulRedisConnection;
import io.lettuce.core.api.reactive.RedisReactiveCommands;
import io.lettuce.core.api.reactive.RedisReactiveListCommands;
import org.reactivestreams.Publisher;
import org.reactivestreams.Subscriber;
import org.reactivestreams.Subscription;
import reactor.core.publisher.Flux;

import java.util.List;
import java.util.concurrent.atomic.AtomicLong;

public class RedisPublisher {

    private RedisClient client;
    private StatefulRedisConnection<String, String> connection;

    public RedisPublisher() {
        client = RedisClient.create("redis://localhost");
        connection = client.connect();
        connection.sync().flushall();
    }

    public Publisher<String> createPublisher(long elements) {
        if (elements == Long.MAX_VALUE) {
            return null;
        }

        RedisReactiveCommands<String, String> commands = connection.reactive();
        RedisReactiveListCommands<String, String> listCommands = commands.listCommands();

        AtomicLong counter = new AtomicLong(0);
        return Flux.create(sink -> {
            for (long i = 0; i < elements; i++) {
                listCommands.rpush("mylist", "element" + i);
                counter.incrementAndGet();
            }

            sink.onRequest(n -> {
                if (n > 0) {
                    List<String> elementsFromList = listCommands.lrange("mylist", 0, n);
                    elementsFromList.forEach(sink::next);
                    sink.onComplete();
                }
            });
        });
    }

    public void close() {
        connection.close();
        client.shutdown();
    }
}
