import redis.clients.jedis.*;
import redis.clients.jedis.exceptions.JedisException;

import java.util.List;

public class ReliableTransaction {
    private Queue<Response<?>> pipelinedResponses;
    private Connection connection;
    private boolean closeConnection;
    private boolean broken;
    private boolean inWatch;
    private boolean inMulti;
    private CommandObjects commandObjects;

    public ReliableTransaction(Connection connection, boolean closeConnection, boolean broken, CommandObjects commandObjects) {
        this.connection = connection;
        this.closeConnection = closeConnection;
        this.broken = broken;
        this.commandObjects = commandObjects;
    }

    public void multi() {
        if (inMulti) {
            throw new JedisException("MULTI calls can not be nested");
        }
        inMulti = true;
        connection.sendCommand(CommandType.MULTI);
    }

    public String watch(String... keys) {
        if (!inMulti) {
            throw new JedisException("WATCH can only be called inside a MULTI/EXEC block");
        }
        if (inWatch) {
            throw new JedisException("WATCH already issued in this transaction");
        }
        inWatch = true;
        return (String) connection.sendCommand(CommandType.WATCH, keys);
    }

    public String unwatch() {
        if (!inMulti) {
            throw new JedisException("UNWATCH can only be called inside a MULTI/EXEC block");
        }
        if (!inWatch) {
            throw new JedisException("There is no WATCH instruction to UNWATCH");
        }
        inWatch = false;
        return (String) connection.sendCommand(CommandType.UNWATCH);
    }

    public <T> Response<T> appendCommand(CommandObject<T> commandObject) {
        if (!inMulti) {
            throw new JedisException("APPEND_COMMAND can only be called inside a MULTI/EXEC block");
        }
        Response<T> response = commandObject.createResponse(connection);
        pipelinedResponses.add(response);
        connection.sendCommand(commandObject);
        return response;
    }

    public void close() {
        if (broken) {
            return;
        }
        if (inMulti) {
            discard();
        }
        if (inWatch) {
            unwatch();
        }
        if (closeConnection) {
            connection.close();
        }
    }

    public void clear() {
        if (inMulti) {
            discard();
        }
        if (inWatch) {
            unwatch();
        }
    }

    public List<Object> exec() {
        if (!inMulti) {
            throw new JedisException("EXEC can only be called inside a MULTI/EXEC block");
        }
        inMulti = false;
        inWatch = false;
        List<Object> execResults = connection.sendCommand(CommandType.EXEC);
        pipelinedResponses.clear();
        return execResults;
    }

    public String discard() {
        if (!inMulti) {
            throw new JedisException("DISCARD can only be called inside a MULTI/EXEC block");
        }
        inMulti = false;
        inWatch = false;
        return (String) connection.sendCommand(CommandType.DISCARD);
    }
}
