import redis.clients.jedis.*;
import redis.clients.jedis.exceptions.JedisException;
import redis.clients.jedis.util.JedisClusterCRC16;
import redis.clients.jedis.util.JedisClusterInfoCache;
import redis.clients.jedis.util.Pool;

import java.util.*;

public class ClusterConnectionProvider {

    private JedisClusterInfoCache cache;

    public ClusterConnectionProvider(Set<HostAndPort> jedisClusterNode, JedisClientConfig jedisClientConfig) {
        this.cache = new JedisClusterInfoCache(jedisClusterNode, jedisClientConfig);
        initializeSlotsCache(jedisClusterNode, jedisClientConfig);
    }

    public Connection getConnection(CommandArguments commandArguments) {
        int slot = JedisClusterCRC16.getSlot(commandArguments.getKey());
        return getConnectionFromSlot(slot);
    }

    public Connection getConnection() {
        List<Connection> connections = new ArrayList<>(cache.getNodes().values());
        Collections.shuffle(connections);
        return connections.get(0);
    }

    public Connection getConnectionFromSlot(int slot) {
        List<JedisPool> jedisPools = cache.getSlotPool(slot);
        Collections.shuffle(jedisPools);
        return jedisPools.get(0).getResource();
    }

    public void renewSlotCache() {
        cache.renewClusterSlots(null);
    }

    public void close() {
        cache.reset();
    }

    public Map<String, ConnectionPool> getConnectionMap() {
        return Collections.unmodifiableMap(cache.getNodes());
    }

    private void initializeSlotsCache(Set<HostAndPort> jedisClusterNode, JedisClientConfig jedisClientConfig) {
        for (HostAndPort node : jedisClusterNode) {
            try (Jedis jedis = new Jedis(node.getHost(), node.getPort(), jedisClientConfig)) {
                cache.discoverClusterSlots(jedis);
            } catch (JedisException e) {
                // Handle exception
            }
        }
    }
}
