import redis.clients.jedis.*;
import redis.clients.jedis.exceptions.JedisConnectionException;
import redis.clients.jedis.exceptions.JedisException;
import org.apache.commons.pool2.impl.GenericObjectPoolConfig;
import java.util.Set;
import java.util.HashSet;

public class SentineledConnectionProvider {

    private final String masterName;
    private final JedisClientConfig masterConfig;
    private final Set<HostAndPort> sentinels;
    private final JedisClientConfig sentinelConfig;

    public SentineledConnectionProvider(String masterName, JedisClientConfig masterConfig,
                                      Set<HostAndPort> sentinels, JedisClientConfig sentinelConfig) {
        this.masterName = masterName;
        this.masterConfig = masterConfig;
        this.sentinels = sentinels;
        this.sentinelConfig = sentinelConfig;
    }

    public Connection getConnection() {
        try {
            HostAndPort master = discoverMaster();
            Connection connection = new Connection(master, masterConfig);
            connection.connect();
            return connection;
        } catch (JedisException e) {
            throw new JedisConnectionException("Failed to get connection from sentinel", e);
        }
    }

    private HostAndPort discoverMaster() {
        for (HostAndPort sentinel : sentinels) {
            try (Jedis jedis = new Jedis(sentinel, sentinelConfig)) {
                String masterHost = jedis.sentinelGetMasterAddrByName(masterName).get(0);
                int masterPort = Integer.parseInt(jedis.sentinelGetMasterAddrByName(masterName).get(1));
                return new HostAndPort(masterHost, masterPort);
            } catch (Exception e) {
                continue;
            }
        }
        throw new JedisException("Could not discover master from sentinels");
    }

    public void close() {
        // Cleanup resources if needed
    }
}

