public class RedisSearchClient {
    private JedisPooled jedis;

    public RedisSearchClient(String host, int port) {
        this.jedis = new JedisPooled(host, port);
    }

    public void createIndex(String indexName, String prefix, SchemaField[] schema) {
        Schema redisSchema = new Schema();
        for (SchemaField field : schema) {
            redisSchema.addTextField(field.getName());
        }
        Index index = new Index(indexName, prefix, redisSchema);
        jedis.ftCreate(index);
    }

    public void addDocument(String key, Object document) {
        jedis.jsonSet(key, document);
    }

    public List<Document> search(String indexName, Query query) {
        SearchResult result = jedis.ftSearch(indexName, query);
        return result.getDocuments();
    }

    public AggregationResult aggregate(String indexName, AggregationBuilder aggregationBuilder) {
        AggregationResult result = jedis.ftAggregate(indexName, aggregationBuilder);
        return result;
    }

    public void close() {
        jedis.close();
    }
}
