        JedisPoolConfig poolConfig = new JedisPoolConfig();
        poolConfig.setMaxTotal(50);
        poolConfig.setMaxIdle(10);
        poolConfig.setMinIdle(1);
        poolConfig.setTestOnBorrow(true);
        poolConfig.setTestOnReturn(true);
        poolConfig.setTestWhileIdle(true);
        poolConfig.setMinEvictableIdleTimeMillis(60000);
        poolConfig.setTimeBetweenEvictionRunsMillis(30000);
        poolConfig.setNumTestsPerEvictionRun(-1);
        pool = new JedisPool(poolConfig, endpoint.getHost(), endpoint.getPort(), 2000, endpoint.getPassword());
    }

    public void executeOperations(int totalOperations) {
        ExecutorService executor = Executors.newFixedThreadPool(50);
        for (int i = 0; i < totalOperations; i++) {
            executor.submit(() -> {
                try (Jedis jedis = pool.getResource()) {
                    jedis.set("key" + Thread.currentThread().getId(), "value" + Thread.currentThread().getId());
