import io.lettuce.core.pubsub.RedisPubSubAdapter;
import io.lettuce.core.pubsub.StatefulRedisPubSubConnection;
import io.lettuce.core.pubsub.api.sync.RedisPubSubCommands;

import java.util.concurrent.atomic.AtomicLong;

public class RedisInteraction {

    private RedisClient client;
    private StatefulRedisConnection<String, String> connection;

    public void initializeConnection() {
        client = RedisClient.create("redis://localhost");
        connection = client.connect();
        RedisCommands<String, String> syncCommands = connection.sync();
        syncCommands.flushall();
    }

    public void closeConnection() {
        connection.close();
        client.shutdown();
    }

    public Publisher<String> createRedisPublisher(long elements) {
        if (elements == Long.MAX_VALUE) {
            return null;
        }

        StatefulRedisPubSubConnection<String, String> pubSubConnection = client.connectPubSub();
        RedisPubSubCommands<String, String> pubSubCommands = pubSubConnection.sync();

        AtomicLong counter = new AtomicLong(0);
        for (long i = 0; i < elements; i++) {
            pubSubCommands.publish("channel", "key" + i);
            counter.incrementAndGet();
        }

        return new Publisher<>(pubSubConnection, counter);
    }

    private static class Publisher<T> extends RedisPubSubAdapter<String, String> {
        private final StatefulRedisPubSubConnection<String, String> connection;
        private final AtomicLong counter;

        public Publisher(StatefulRedisPubSubConnection<String, String> connection, AtomicLong counter) {
            this.connection = connection;
            this.counter = counter;
        }

        @Override
        public void message(String channel, String message) {
            if (counter.decrementAndGet() == 0) {
                connection.close();
            }
        }
    }
}
