        }
    }

    public boolean isClosed() {
        return pool == null || pool.isClosed();
    }

    public void destroy() {
        if (pool != null) {
            pool.destroy();
        }
    }

    public static Set<String> toStrings(Set<HostAndPort> hostAndPorts) {
        Set<String> strings = new HashSet<>();
        for (HostAndPort hostAndPort : hostAndPorts) {
            strings.add(hostAndPort.toString());
        }
        return strings;
    }
}
