import java.io.IOException;
import java.nio.ByteBuffer;
import java.nio.channels.FileChannel;
import java.nio.file.Files;
import java.nio.file.Path;
import java.nio.file.StandardOpenOption;

public class WaveWriter implements AutoCloseable {
    private AudioFormat mAudioFormat;
    private int mFileRolloverCounter;
    private long mMaxSize;
    private Path mFile;
    private FileChannel mFileChannel;
    private boolean mDataChunkOpen;
    private long mDataChunkSizeOffset;
    private int mDataChunkSize;

    public WaveWriter(AudioFormat format, Path file, long maxSize) throws IOException {
        mAudioFormat = format;
        mMaxSize = maxSize;
        mFile = file;
        open();
    }

    public WaveWriter(AudioFormat format, Path file) throws IOException {
        this(format, file, 0);
    }

    private void open() throws IOException {
        mFileChannel = FileChannel.open(mFile, StandardOpenOption.CREATE, StandardOpenOption.WRITE);
        mFileChannel.write(getWaveHeader(mAudioFormat));
        mFileChannel.write(getFormatChunk(mAudioFormat));
        mDataChunkOpen = false;
    }

    @Override
    public void close() throws IOException {
        if (mFileChannel != null) {
            if (mDataChunkOpen) {
                closeDataChunk();
            }
            mFileChannel.close();
        }
    }

    public void close(Path path) throws IOException {
        close();
        Files.move(mFile, path);
    }

    public void writeData(ByteBuffer buffer) throws IOException {
        if (mMaxSize > 0 && mFileChannel.size() + buffer.limit() > mMaxSize) {
            rollover();
        }
        if (!mDataChunkOpen) {
            openDataChunk();
        }
        mFileChannel.write(buffer);
        mDataChunkSize += buffer.limit();
    }

    private void closeDataChunk() throws IOException {
        mFileChannel.write(getUnsignedIntegerBuffer(mDataChunkSize));
        mDataChunkOpen = false;
        updateTotalSize();
    }

    private void openDataChunk() throws IOException {
        mDataChunkSize = 0;
        mDataChunkSizeOffset = mFileChannel.position();
        mFileChannel.write(getDataHeader());
        mDataChunkOpen = true;
    }

    private void rollover() throws IOException {
        closeDataChunk();
        updateFileName();
        open();
    }

    private void updateTotalSize() throws IOException {
        long totalSize = mFileChannel.size();
        mFileChannel.position(4);
        mFileChannel.write(getUnsignedIntegerBuffer(totalSize - 8));
        mFileChannel.position(totalSize);
    }

    private void updateDataChunkSize() throws IOException {
        mFileChannel.position(mDataChunkSizeOffset);
        mFileChannel.write(getUnsignedIntegerBuffer(mDataChunkSize));
        mFileChannel.position(mFileChannel.size());
    }

    protected static ByteBuffer getUnsignedIntegerBuffer(long size) {
        ByteBuffer buffer = ByteBuffer.allocate(4);
        buffer.putInt((int) size);
        buffer.flip();
        return buffer;
    }

    private void updateFileName() {
        String fileName = mFile.getFileName().toString();
        int dotIndex = fileName.lastIndexOf('.');
        if (dotIndex != -1) {
            fileName = fileName.substring(0, dotIndex) + "_" + mFileRolloverCounter + fileName.substring(dotIndex);
        } else {
            fileName = fileName + "_" + mFileRolloverCounter;
        }
        mFile = mFile.getParent().resolve(fileName);
        mFileRolloverCounter++;
    }

    public static ByteBuffer getDataHeader() {
        ByteBuffer buffer = ByteBuffer.allocate(8);
        buffer.put((byte) 'd');
        buffer.put((byte) 'a');
        buffer.put((byte) 't');
        buffer.put((byte) 'a');
        buffer.putInt(0);
        buffer.flip();
        return buffer;
    }

    public static ByteBuffer getWaveHeader(AudioFormat format) {
        ByteBuffer buffer = ByteBuffer.allocate(44);
        buffer.put((byte) 'R');
        buffer.put((byte) 'I');
        buffer.put((byte) 'F');
        buffer.put((byte) 'F');
        buffer.putInt(0);
        buffer.put((byte) 'W');
        buffer.put((byte) 'A');
        buffer.put((byte) 'V');
        buffer.put((byte) 'E');
        buffer.put((byte) 'f');
        buffer.put((byte) 'm');
        buffer.put((byte) 't');
        buffer.put((byte) ' ');
        buffer.putInt(16);
        buffer.putShort((short) format.getEncoding());
        buffer.putShort((short) format.getChannels());
        buffer.putInt(format.getSampleRate());
        buffer.putInt(format.getSampleRate() * format.getChannels() * format.getSampleSizeInBits() / 8);
        buffer.putShort((short) (format.getChannels() * format.getSampleSizeInBits() / 8));
        buffer.putShort((short) format.getSampleSizeInBits());
        buffer.put((byte) 'd');
        buffer.put((byte) 'a');
        buffer.put((byte) 't');
        buffer.put((byte) 'a');
        buffer.putInt(0);
        buffer.flip();
        return buffer;
    }

    public static ByteBuffer getFormatChunk(AudioFormat format) {
        ByteBuffer buffer = ByteBuffer.allocate(24);
        buffer.put((byte) 'f');
        buffer.put((byte) 'm');
        buffer.put((byte) 't');
        buffer.put((byte) ' ');
        buffer.putInt(16);
        buffer.putShort((short) format.getEncoding());
        buffer.putShort((short) format.getChannels());
        buffer.putInt(format.getSampleRate());
        buffer.putInt(format.getSampleRate() * format.getChannels() * format.getSampleSizeInBits() / 8);
        buffer.putShort((short) (format.getChannels() * format.getSampleSizeInBits() / 8));
        buffer.putShort((short) format.getSampleSizeInBits());
        buffer.flip();
        return buffer;
    }
