        public JedisClusterClient(JedisCluster jedisCluster) {
            this.jedisCluster = jedisCluster;
        }

        @Override
        public void set(byte[] key, byte[] value) {
            jedisCluster.set(key, value);
        }

        @Override
        public void setex(byte[] key, int seconds, byte[] value) {
            jedisCluster.setex(key, seconds, value);
        }

        @Override
        public void hset(byte[] key, byte[] field, byte[] value) {
            jedisCluster.hset(key, field, value);
        }

        @Override
        public void expire(byte[] key, int seconds) {
            jedisCluster.expire(key, seconds);
        }

        @Override
        public void mset(Collection<MSetParam> params) {
            for (MSetParam param : params) {
                jedisCluster.set(param.getKey(), param.getValue());
                if (param.getSeconds() > 0) {
                    jedisCluster.expire(param.getKey(), param.getSeconds());
                }
            }
        }

        @Override
        public byte[] get(byte[] key) {
            return jedisCluster.get(key);
        }

        @Override
        public byte[] hget(byte[] key, byte[] field) {
            return jedisCluster.hget(key, field);
        }

        @Override
        public <T> Map<CacheKeyTO, CacheWrapper<T>> mget(Type<T> type, Set<CacheKeyTO> keys) {
            Map<CacheKeyTO, CacheWrapper<T>> result = new HashMap<>();
            for (CacheKeyTO key : keys) {
                byte[] value = jedisCluster.get(key.getKey());
                if (value != null) {
                    result.put(key, new CacheWrapper<>(type, value));
                }
            }
            return result;
        }

        @Override
        public void delete(Set<CacheKeyTO> keys) {
            for (CacheKeyTO key : keys) {
                jedisCluster.del(key.getKey());
            }
        }

        public void close() {
            jedisCluster.close();
        }
    }
}
