import redis.clients.jedis.Jedis;
import redis.clients.jedis.JedisPool;
import redis.clients.jedis.params.SetParams;

import java.util.concurrent.TimeUnit;

public class RedisDistributeLock {
    private static JedisPool pool;
    private JedisLock jedisLock;
    private String lockKey;
    private Jedis jedis;
    private int timeoutMsecs;
    private int expireMsecs;

    public RedisDistributeLock(String lockKey) {
        this(lockKey, 3000, 300000);
    }

    public RedisDistributeLock(String lockKey, int timeoutMsecs, int expireMsecs) {
        this.lockKey = lockKey;
        this.timeoutMsecs = timeoutMsecs;
        this.expireMsecs = expireMsecs;
    }

    public void wrap(Runnable runnable) {
        try {
            jedis = pool.getResource();
            jedisLock = new JedisLock(jedis, lockKey, expireMsecs);
            if (jedisLock.acquire(timeoutMsecs, TimeUnit.MILLISECONDS)) {
                runnable.run();
            }
        } catch (InterruptedException e) {
            Thread.currentThread().interrupt();
        } finally {
            lockRelease(jedisLock, jedis);
        }
    }

    private void lockRelease(JedisLock lock, Jedis jedis) {
        if (lock != null) {
            lock.release();
        }
        if (jedis != null) {
            jedis.close();
        }
    }

    public static JedisPool getPool() {
        return pool;
    }

    public static void setPool(JedisPool pool) {
        RedisDistributeLock.pool = pool;
    }

    private static class JedisLock {
        private static final String LOCK_SUCCESS = "OK";
        private static final String SET_IF_NOT_EXIST = "NX";
        private static final String SET_WITH_EXPIRE_TIME = "PX";
        private static final Long RELEASE_SUCCESS = 1L;
        private final Jedis jedis;
        private final String lockKey;
        private final int expireMsecs;

        public JedisLock(Jedis jedis, String lockKey, int expireMsecs) {
            this.jedis = jedis;
            this.lockKey = lockKey;
            this.expireMsecs = expireMsecs;
        }

        public boolean acquire(long timeoutMsecs, TimeUnit unit) throws InterruptedException {
            long nanoTimeout = unit.toNanos(timeoutMsecs);
            do {
                String result = jedis.set(lockKey, "", SetParams.setParams().nx().px(expireMsecs));
                if (LOCK_SUCCESS.equals(result)) {
                    return true;
                }
                if (nanoTimeout <= 0) {
                    return false;
                }
                Thread.sleep(1);
            } while (true);
        }

        public void release() {
            jedis.del(lockKey);
        }
    }
}
