import redis.clients.jedis.*;
import redis.clients.jedis.exceptions.JedisException;

import java.util.concurrent.locks.ReentrantLock;

public class RedisConnectionManager {
    private JedisPooled jedisPool;
    private GenericObjectPoolConfig<Connection> poolConfig;
    private ReentrantLock lock = new ReentrantLock();

    public RedisConnectionManager(HostAndPort hostAndPort, DefaultJedisClientConfig clientConfig) {
        this.jedisPool = new JedisPooled(hostAndPort, clientConfig);
    }

    public RedisConnectionManager(HostAndPort hostAndPort, DefaultJedisClientConfig clientConfig, GenericObjectPoolConfig<Connection> poolConfig) {
        this.jedisPool = new JedisPooled(hostAndPort, clientConfig, poolConfig);
        this.poolConfig = poolConfig;
    }

    public Connection getConnection() {
        try {
            return jedisPool.getResource();
        } catch (JedisException e) {
            throw new RuntimeException("Failed to get Redis connection", e);
        }
    }

    public void closeConnection(Connection connection) {
        try {
            jedisPool.returnResource(connection);
        } catch (JedisException e) {
            throw new RuntimeException("Failed to return Redis connection", e);
        }
    }

    public void setCredentialsProvider(RedisCredentialsProvider credentialsProvider) {
        lock.lock();
        try {
            jedisPool.setConfig(new DefaultJedisClientConfig.Builder()
                    .setCredentialsProvider(credentialsProvider)
                    .build());
        } finally {
            lock.unlock();
        }
    }

    public void executeCommand(String key, String value) {
        try (Connection connection = getConnection()) {
            connection.set(key, value);
        }
    }

    public String getValue(String key) {
        try (Connection connection = getConnection()) {
            return connection.get(key);
        }
