import redis.clients.jedis.HostAndPort;
import redis.clients.jedis.JedisClientConfig;
import redis.clients.jedis.exceptions.JedisException;
import redis.clients.jedis.JedisPool;
import redis.clients.jedis.JedisSentinelPool;
import redis.clients.jedis.exceptions.JedisConnectionException;

import java.util.Set;
import java.util.concurrent.atomic.AtomicReference;

public class SentineledConnectionProvider {
    private final AtomicReference<HostAndPort> currentMaster = new AtomicReference<>();
    private final JedisPool pool;
    private final String masterName;
    private final JedisClientConfig masterClientConfig;
    private final JedisClientConfig sentinelClientConfig;

    public SentineledConnectionProvider(String masterName, JedisClientConfig masterClientConfig, Set<HostAndPort> sentinels, JedisClientConfig sentinelClientConfig) {
        this.masterName = masterName;
        this.masterClientConfig = masterClientConfig;
        this.sentinelClientConfig = sentinelClientConfig;
        this.pool = new JedisSentinelPool(masterName, sentinels, sentinelClientConfig);
        this.currentMaster.set(initSentinels(sentinels));
        new SentinelListener(currentMaster.get()).start();
    }

    public Connection getConnection() {
        HostAndPort master = currentMaster.get();
        try {
            return pool.getResource();
        } catch (JedisConnectionException e) {
            if (master.equals(currentMaster.get())) {
                throw e;
            }
            return pool.getResource();
        }
    }

    private void initMaster(HostAndPort master) {
        if (!master.equals(currentMaster.get())) {
            currentMaster.set(master);
            pool.destroy();
            pool.initialize();
        }
    }

    private HostAndPort initSentinels(Set<HostAndPort> sentinels) {
        for (HostAndPort sentinel : sentinels) {
            try (Jedis jedis = new Jedis(sentinel, sentinelClientConfig)) {
                Set<String> masterAddresses = jedis.sentinelGetMasterAddrByName(masterName);
                if (masterAddresses.size() == 2) {
                    return new HostAndPort(masterAddresses.toArray(new String[0]));
                }
            } catch (JedisException e) {
                // Ignore and try next sentinel
            }
        }
        throw new RuntimeException("Could not discover master address from any sentinel");
    }

    private class SentinelListener extends Thread {
        private final HostAndPort node;

        public SentinelListener(HostAndPort node) {
            this.node = node;
        }

        @Override
        public void run() {
            try (Jedis jedis = new Jedis(node, sentinelClientConfig)) {
                jedis.subscribe(new JedisPubSub() {
                    @Override
                    public void onMessage(String channel, String message) {
                        if (channel.equals("sentinel") && message.startsWith("+switch-master")) {
                            String[] parts = message.split(" ");
                            if (parts[1].equals(masterName)) {
                                initMaster(new HostAndPort(parts[3], Integer.parseInt(parts[4])));
                            }
                        }
                    }
                });
            }
        }

        public void shutdown() {
            interrupt();
        }
    }
}
