import java.util.concurrent.ExecutorService;

public class RedisBroadcastService {

    private final ObjectMapper objectMapper;
    private final ExecutorService executorService;
    private final String channel;
    private final Jedis subscriber;
    private final Jedis publisher;
    private final String id;

    public RedisBroadcastService(Config config, ExecutorService executorService, ObjectMapper objectMapper) {
        this.objectMapper = objectMapper;
        this.executorService = executorService;
        this.channel = config.getChannel();
        this.id = config.getId();
        this.subscriber = new Jedis(config.getRedisHost(), config.getRedisPort());
        this.publisher = new Jedis(config.getRedisHost(), config.getRedisPort());
    }

    public void sendMessage(BroadcastMessage message) {
        try {
            String serializedMessage = objectMapper.writeValueAsString(message);
            publisher.publish(channel, serializedMessage);
        } catch (Exception e) {
            throw new RuntimeException("Failed to send message", e);
        }
    }

    public void start() {
        subscriber.subscribe(new JedisPubSub() {
            @Override
            public void onMessage(String channel, String message) {
                if (!message.contains(id)) {
                    try {
                        BroadcastMessage broadcastMessage = objectMapper.readValue(message, BroadcastMessage.class);
                        executorService.submit(() -> {
                            broadcastMessage.getHandler().handle(broadcastMessage);
                        });
                    } catch (Exception e) {
                        throw new RuntimeException("Failed to deserialize message", e);
                    }
                }
            }
        }, channel);
    }

    public void stop() {
        subscriber.unsubscribe();
        subscriber.close();
        publisher.close();
    }

    private class Receiver implements Runnable {
        @Override
        public void run() {
            subscriber.subscribe(new JedisPubSub() {
                @Override
                public void onMessage(String channel, String message) {
                    if (!message.contains(id)) {
                        try {
                            BroadcastMessage broadcastMessage = objectMapper.readValue(message, BroadcastMessage.class);
                            executorService.submit(() -> {
                                broadcastMessage.getHandler().handle(broadcastMessage);
                            });
                        } catch (Exception e) {
                            throw new RuntimeException("Failed to deserialize message", e);
                        }
                    }
                }
            }, channel);
        }
    }
}
