import java.io.IOException;
import java.nio.MappedByteBuffer;
import java.nio.channels.FileChannel;
import java.nio.file.Path;
import java.nio.file.StandardOpenOption;

public class ArrayLongLongMapMmap {
    private final Path path;
    private final FileChannel writeChannel;
    private MappedByteBuffer[] segmentsArray;
    private FileChannel readChannel;

    public ArrayLongLongMapMmap(Path path, boolean madvise) throws IOException {
        this.path = path;
        this.writeChannel = FileChannel.open(path, StandardOpenOption.CREATE, StandardOpenOption.READ, StandardOpenOption.WRITE);
    }

    public void init() throws IOException {
        writeChannel.close();
        readChannel = FileChannel.open(path, StandardOpenOption.READ);
        segmentsArray = new MappedByteBuffer[(int) (readChannel.size() / (1 << 27))];
        for (int i = 0; i < segmentsArray.length; i++) {
            segmentsArray[i] = readChannel.map(FileChannel.MapMode.READ_ONLY, i * (1 << 27), Math.min(1 << 27, readChannel.size() - i * (1 << 27)));
        }
    }

    public long get(long key) {
        int segmentIndex = (int) (key >> 27);
        int offset = (int) (key & ((1 << 27) - 1));
        return segmentsArray[segmentIndex].getLong(offset * 8);
    }

    public void close() throws IOException {
        for (MappedByteBuffer segment : segmentsArray) {
            segment.force();
            segment.clear();
        }
        readChannel.close();
        writeChannel.deleteIfExists();
    }
}
