import java.io.File;
import java.io.IOException;
import java.nio.MappedByteBuffer;
import java.nio.channels.FileChannel;
import java.nio.file.Files;
import java.nio.file.Path;
import java.nio.file.StandardOpenOption;
import java.util.concurrent.atomic.AtomicBoolean;
import java.util.function.Consumer;
import java.util.function.IntConsumer;
import org.agrona.EpochClock;
import org.agrona.ExpandableArrayBuffer;
import org.agrona.concurrent.UnsafeBuffer;

public class MarkFile implements AutoCloseable {
    private final File markFile;
    private final MappedByteBuffer mappedBuffer;
    private final UnsafeBuffer buffer;
    private final AtomicBoolean isClosed = new AtomicBoolean();

    public MarkFile(
        final File markFile,
        final boolean createIfAbsent,
        final int version,
        final int heartbeatIntervalMillis,
        final int timestampFieldOffset,
        final long initialTimestamp,
        final EpochClock epochClock,
        final IntConsumer versionMismatchHandler,
        final Consumer<String> errorHandler
    ) throws IOException {
        this.markFile = markFile;
        this.mappedBuffer = mapNewOrExistingMarkFile(
            markFile,
            createIfAbsent,
            version,
            heartbeatIntervalMillis,
            timestampFieldOffset,
            initialTimestamp,
            epochClock,
            versionMismatchHandler,
            errorHandler
        );
        this.buffer = new UnsafeBuffer(mappedBuffer);
    }

    @Override
    public void close() {
        if (isClosed.compareAndSet(false, true)) {
            try {
                mappedBuffer.force();
            } catch (final IOException ex) {
                throw new RuntimeException(ex);
            } finally {
                try {
                    mappedBuffer.close();
                } catch (final IOException ex) {
                    throw new RuntimeException(ex);
                }
            }
        }
    }

    public static MappedByteBuffer mapNewOrExistingMarkFile(
        final File markFile,
        final boolean createIfAbsent,
        final int version,
        final int heartbeatIntervalMillis,
        final int timestampFieldOffset,
        final long initialTimestamp,
        final EpochClock epochClock,
        final IntConsumer versionMismatchHandler,
        final Consumer<String> errorHandler
    ) throws IOException {
        final Path markFilePath = markFile.toPath();
        final File markFileDirectory = markFilePath.getParent().toFile();
        ensureMarkFileLink(markFileDirectory, markFilePath, markFile.getName());

        final FileChannel fileChannel = FileChannel.open(
            markFilePath,
            createIfAbsent ? StandardOpenOption.CREATE : StandardOpenOption.READ,
            StandardOpenOption.WRITE
        );

        final long fileSize = fileChannel.size();
        final MappedByteBuffer mappedBuffer;
        if (fileSize == 0) {
            if (!createIfAbsent) {
                throw new IOException("Mark file does not exist");
            }
            mappedBuffer = fileChannel.map(FileChannel.MapMode.READ_WRITE, 0, MarkFileUtil.MARK_FILE_LENGTH);
            MarkFileUtil.writeVersion(mappedBuffer, version);
            MarkFileUtil.writeHeartbeatIntervalMillis(mappedBuffer, heartbeatIntervalMillis);
            MarkFileUtil.writeTimestamp(mappedBuffer, timestampFieldOffset, initialTimestamp);
        } else {
            mappedBuffer = fileChannel.map(FileChannel.MapMode.READ_WRITE, 0, MarkFileUtil.MARK_FILE_LENGTH);
            final int fileVersion = MarkFileUtil.readVersion(mappedBuffer);
            if (fileVersion != version) {
                versionMismatchHandler.accept(fileVersion);
            }
            final int fileHeartbeatIntervalMillis = MarkFileUtil.readHeartbeatIntervalMillis(mappedBuffer);
            if (fileHeartbeatIntervalMillis != heartbeatIntervalMillis) {
                errorHandler.accept("Heartbeat interval mismatch");
            }
            final long fileTimestamp = MarkFileUtil.readTimestamp(mappedBuffer, timestampFieldOffset);
            if (fileTimestamp < epochClock.time()) {
                errorHandler.accept("Timestamp is in the past");
            }
        }
        return mappedBuffer;
    }

    public static void ensureMarkFileLink(
        final File markFileDirectory,
        final File markFilePath,
        final String markFileName
    ) throws IOException {
        if (!markFileDirectory.exists()) {
            if (!markFileDirectory.mkdirs()) {
                throw new IOException("Failed to create mark file directory");
            }
        }
        final File markFileLink = new File(markFileDirectory, markFileName);
        if (!markFileLink.exists()) {
            Files.createSymbolicLink(markFileLink.toPath(), markFilePath.toPath());
        }
    }
}
