                        logger.error("Failed to process message: {}", new String(msg.getBody()), e);
                        // Retry logic
                        if (msg.getReconsumeTimes() < 3) {
                            return ConsumeConcurrentlyStatus.RECONSUME_LATER;
                        } else {
                            logger.error("Max retry attempts reached for message: {}", new String(msg.getBody()));
                            return ConsumeConcurrentlyStatus.CONSUME_SUCCESS;
                        }
                    }
                }
                return ConsumeConcurrentlyStatus.CONSUME_SUCCESS;
            }
        });
        consumer.start();
    }
}
