import io.undertow.server.HttpServerExchange;
import io.undertow.util.AttachmentKey;
import io.undertow.util.Headers;
import io.undertow.util.SameThreadExecutor;
import org.xnio.ChannelExceptionHandler;
import org.xnio.ChannelListener;
import org.xnio.IoUtils;
import org.xnio.PooledByteBuffer;
import org.xnio.channels.Channels;
import org.xnio.channels.StreamSinkChannel;

import java.io.IOException;
import java.nio.ByteBuffer;
import java.nio.channels.FileChannel;
import java.nio.file.Path;
import java.nio.file.StandardOpenOption;
import java.util.concurrent.TimeUnit;

public class PathResource {
    private static final AttachmentKey<PathResource> ATTACHMENT_KEY = AttachmentKey.create(PathResource.class);
    private final Path file;
    private final PathResourceManager manager;

    public PathResource(Path file, PathResourceManager manager) {
        this.file = file;
        this.manager = manager;
    }

    public void serve(Sender sender, HttpServerExchange exchange, IoCallback callback) {
        exchange.putAttachment(ATTACHMENT_KEY, this);
        long contentLength = getContentLength();
        if (contentLength == null) {
            callback.onException(sender, exchange, new IOException("Unable to determine file size"));
            return;
        }
        if (exchange.getRequestHeaders().contains(Headers.RANGE)) {
            String range = exchange.getRequestHeaders().getFirst(Headers.RANGE);
            long start = 0;
            long end = contentLength - 1;
            if (range != null) {
                String[] parts = range.split("=");
                if (parts.length == 2 && parts[0].equals("bytes")) {
                    String[] rangeParts = parts[1].split("-");
                    if (rangeParts.length == 1) {
                        start = Long.parseLong(rangeParts[0]);
                    } else if (rangeParts.length == 2) {
                        start = Long.parseLong(rangeParts[0]);
                        end = Long.parseLong(rangeParts[1]);
                    }
                }
            }
            if (start < 0 || end < 0 || start > end || start >= contentLength) {
                exchange.setResponseCode(416);
                callback.onComplete(sender, exchange);
                return;
            }
            serveRange(sender, exchange, start, end, callback);
        } else {
            serveImpl(sender, exchange, 0, contentLength - 1, callback, true);
        }
    }

    private void serveRange(Sender sender, HttpServerExchange exchange, long start, long end, IoCallback callback) {
        exchange.setResponseCode(206);
        exchange.getResponseHeaders().put(Headers.CONTENT_RANGE, "bytes " + start + "-" + end + "/" + getContentLength());
        exchange.getResponseHeaders().put(Headers.CONTENT_LENGTH, String.valueOf(end - start + 1));
        serveImpl(sender, exchange, start, end, callback, false);
    }

    private Long getContentLength() {
        try {
            return file.toFile().length();
        } catch (SecurityException e) {
            return null;
        }
    }

    private void serveImpl(Sender sender, HttpServerExchange exchange, long start, long end, IoCallback callback, boolean closeChannel) {
        FileChannel channel = null;
        try {
            channel = FileChannel.open(file, StandardOpenOption.READ);
            channel.position(start);
            if (end - start + 1 < manager.getTransferSizeThreshold()) {
                new BaseFileTask(sender, exchange, channel, start, end, callback, closeChannel).start();
            } else {
                new ServerTask(sender, exchange, channel, start, end, callback, closeChannel).start();
            }
        } catch (IOException e) {
            callback.onException(sender, exchange, e);
            IoUtils.safeClose(channel);
        }
    }

    private static class BaseFileTask implements ChannelListener<StreamSinkChannel> {
        private final Sender sender;
        private final HttpServerExchange exchange;
        private final FileChannel channel;
        private final long start;
        private final long end;
        private final IoCallback callback;
        private final boolean closeChannel;
        private long position;

        public BaseFileTask(Sender sender, HttpServerExchange exchange, FileChannel channel, long start, long end, IoCallback callback, boolean closeChannel) {
            this.sender = sender;
            this.exchange = exchange;
            this.channel = channel;
            this.start = start;
            this.end = end;
            this.callback = callback;
            this.closeChannel = closeChannel;
            this.position = start;
        }

        public void start() {
            Channels.write(channel, position, end - position + 1, exchange.getResponseChannel(), new TransferTask(sender, exchange, channel, start, end, callback, closeChannel), ChannelExceptionHandler.CLOSE, SameThreadExecutor.INSTANCE);
        }

        @Override
        public void handleEvent(StreamSinkChannel channel) {
            if (position > end) {
                callback.onComplete(sender, exchange);
                if (closeChannel) {
                    IoUtils.safeClose(channel);
                }
            } else {
                Channels.write(channel, position, end - position + 1, exchange.getResponseChannel(), this, ChannelExceptionHandler.CLOSE, SameThreadExecutor.INSTANCE);
            }
        }
    }

    private static class ServerTask extends BaseFileTask {
        private final PooledByteBuffer buffer;

        public ServerTask(Sender sender, HttpServerExchange exchange, FileChannel channel, long start, long end, IoCallback callback, boolean closeChannel) {
            super(sender, exchange, channel, start, end, callback, closeChannel);
            this.buffer = exchange.getConnection().getByteBufferPool().allocate(manager.getTransferSizeThreshold());
        }

        @Override
        public void start() {
            try {
                int read = channel.read(buffer.getBuffer());
                if (read == -1) {
                    callback.onComplete(sender, exchange);
                    if (closeChannel) {
                        IoUtils.safeClose(channel);
                    }
                } else {
                    buffer.getBuffer().flip();
                    Channels.write(exchange.getResponseChannel(), buffer.getBuffer(), this, ChannelExceptionHandler.CLOSE, SameThreadExecutor.INSTANCE);
                }
            } catch (IOException e) {
                callback.onException(sender, exchange, e);
                IoUtils.safeClose(channel);
                buffer.close();
            }
        }

        @Override
        public void handleEvent(StreamSinkChannel channel) {
            try {
                int read = channel.read(buffer.getBuffer());
                if (read == -1) {
                    buffer.close();
                    callback.onComplete(sender, exchange);
                    if (closeChannel) {
                        IoUtils.safeClose(channel);
                    }
                } else {
                    buffer.getBuffer().flip();
                    Channels.write(exchange.getResponseChannel(), buffer.getBuffer(), this, ChannelExceptionHandler.CLOSE, SameThreadExecutor.INSTANCE);
                }
            } catch (IOException e) {
                callback.onException(sender, exchange, e);
                IoUtils.safeClose(channel);
                buffer.close();
            }
        }
    }

    private static class TransferTask implements ChannelListener<StreamSinkChannel> {
        private final Sender sender;
        private final HttpServerExchange exchange;
        private final FileChannel channel;
        private final long start;
        private final long end;
        private final IoCallback callback;
        private final boolean closeChannel;
        private long position;

        public TransferTask(Sender sender, HttpServerExchange exchange, FileChannel channel, long start, long end, IoCallback callback, boolean closeChannel) {
            this.sender = sender;
            this.exchange = exchange;
            this.channel = channel;
            this.start = start;
            this.end = end;
            this.callback = callback;
            this.closeChannel = closeChannel;
            this.position = start;
        }

        @Override
        public void handleEvent(StreamSinkChannel channel) {
            if (position > end) {
                callback.onComplete(sender, exchange);
                if (closeChannel) {
                    IoUtils.safeClose(channel);
                }
            } else {
                Channels.transferFrom(channel, channel, position, end - position + 1, TimeUnit.MILLISECONDS, 100, this, ChannelExceptionHandler.CLOSE, SameThreadExecutor.INSTANCE);
            }
        }
    }
}
