public class GSSClient {
    private Socket s;

    public static void main(String[] args) {
        GSSClient client = new GSSClient();
        try {
            client.main(args);
        } catch (Exception e) {
            e.printStackTrace();
        }
    }

    public void main(String[] args) throws Exception {
        if (args.length != 2) {
            usage();
            return;
        }
        String serverName = args[0];
        int serverPort = Integer.parseInt(args[1]);
        s = new Socket(serverName, serverPort);
        GSSContext aCtxt = createCtxt(serverName, new Oid("1.2.840.113554.1.2.2"));
        sendMsgToPeer(aCtxt, "Hello, Server!");
        verifyMsgFromPeer(aCtxt, "Hello, Client!");
        aCtxt.dispose();
        s.close();
    }

    public GSSContext createCtxt(String serverName, Oid mechOid) throws Exception {
        print("Creating GSSContext..");
        GSSManager manager = GSSManager.getInstance();
        GSSName serverNameObj = manager.createName(serverName + "/krbtgt/" + serverName.toUpperCase(), null);
        GSSCredential clientCred = new KerberosTicketCredential(serverNameObj, "HTTP", "EXAMPLE.COM", "HTTP/" + serverName, "password", 0, 0, null, null);
        GSSContext aCtxt = manager.createContext(serverNameObj, mechOid, clientCred, GSSContext.DEFAULT_LIFETIME);
        aCtxt.requestMutualAuth(true);
        aCtxt.requestReplayDet(true);
        aCtxt.requestSequenceDet(true);
        aCtxt.requestConf(true);
        aCtxt.requestInteg(true);
        aCtxt.requestCredDeleg(false);
        print("GSSContext established.");
        return aCtxt;
    }

    public void sendMsgToPeer(GSSContext aCtxt, String msg) throws Exception {
        print("Sending message to peer..");
        byte[] inToken = new byte[0];
        byte[] outToken = aCtxt.initSecContext(inToken, 0, inToken.length);
        DataOutputStream out = new DataOutputStream(s.getOutputStream());
        out.writeInt(outToken.length);
        out.write(outToken);
        out.flush();
        print("Message sent.");
    }

    public void verifyMsgFromPeer(GSSContext aCtxt, String msg) throws Exception {
        print("Verifying message from peer..");
        DataInputStream in = new DataInputStream(s.getInputStream());
        int tokenLength = in.readInt();
        byte[] inToken = new byte[tokenLength];
        in.readFully(inToken, 0, tokenLength);
        aCtxt.acceptSecContext(inToken, 0, inToken.length);
        print("Message verified.");
    }

    public void usage() {
        print("Usage: java GSSClient <server name> <server port>");
    }

    public void print(String msg) {
        System.out.println(msg);
    }

    public void exit(int status) {
        try {
            s.close();
        } catch (Exception e) {
            e.printStackTrace();
        }
        System.exit(status);
    }
