import java.io.*;
import java.net.*;
import javax.servlet.*;
import javax.servlet.http.*;

public class Lisplet extends HttpServlet {
    private String host;
    private int port;

    public void doPost(HttpServletRequest req, HttpServletResponse resp) throws ServletException, IOException {
        Socket sock = new Socket(host, port);
        PushbackInputStream in = new PushbackInputStream(sock.getInputStream());
        Writer writer = new OutputStreamWriter(sock.getOutputStream());
        sendRequest(req, writer);
        if (getResponseHeader(req, resp, in)) {
            transferResponseBody(resp, in);
        }
        writer.close();
        in.close();
        sock.close();
    }

    private void transferResponseBody(HttpServletResponse resp, PushbackInputStream strm) throws IOException {
        OutputStream out = resp.getOutputStream();
        byte[] buf = new byte[1024];
        int len;
        while ((len = strm.read(buf)) != -1) {
            out.write(buf, 0, len);
        }
        out.flush();
    }

    private boolean getResponseHeader(HttpServletRequest req, HttpServletResponse resp, PushbackInputStream strm) throws IOException {
        int ch;
        while ((ch = strm.read()) != -1) {
            if (ch == '\r') {
                if (strm.read() == '\n') {
                    return true;
                }
            }
        }
        return false;
    }

    private void sendRequest(HttpServletRequest req, Writer writer) throws IOException {
        writer.write("POST ");
        writer.write(req.getRequestURI());
        writer.write(" HTTP/1.1\r\n");
        writer.write("Host: ");
        writer.write(req.getServerName());
        writer.write("\r\n");
        writer.write("Content-Length: ");
        writer.write(Integer.toString(req.getContentLength()));
        writer.write("\r\n");
        writer.write("Content-Type: ");
        writer.write(req.getContentType());
        writer.write("\r\n");
        writer.write("Connection: close\r\n");
        writer.write("\r\n");
        writer.flush();
        InputStream in = req.getInputStream();
        byte[] buf = new byte[1024];
        int len;
        while ((len = in.read(buf)) != -1) {
            writer.write(new String(buf, 0, len));
        }
        writer.write("\r\n");
        writer.flush();
    }

    private void writeTagValuePair(Writer writer, String tag, String value) throws IOException {
        writer.write(tag);
        writer.write(": ");
        writer.write(value);
        writer.write("\r\n");
    }
}
