    }

    private class ForwardingPrintStream extends PrintStream {
        public ForwardingPrintStream(String type, ObjectOutputStream oos) {
            super(new ByteArrayOutputStream());
            this.oos = oos;
            this.type = type;
        }

        private final ObjectOutputStream oos;
        private final String type;

        @Override
        public void flush() {
            super.flush();
            if (resetToGo > 0) {
                resetToGo--;
            } else {
                pushFrame(new OutputFrame(type, ((ByteArrayOutputStream) out).toByteArray()));
                ((ByteArrayOutputStream) out).reset();
            }
        }
    }
