        byte[] bytes = new byte[BYTE_SIZE_DATA];
        int len = 0;
        while((len=fis.read(bytes)) != -1){
            mOutputStream.write(bytes, 0, len);
            if(mOnSendListener != null) mOnSendListener.onProgress(fis.available(), mFileInfo.getSize());
        }
        mOutputStream.flush();
        mOutputStream.close();
    }

    @Override
    public void finish() {
        try {
            if(mOutputStream != null) mOutputStream.close();
            if(mSocket != null && mSocket.isConnected()) mSocket.close();
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    public interface OnSendListener {
        void onStart();
        void onProgress(long progress, long total);
        void onSuccess(FileInfo fileInfo);
        void onFailure(Throwable t, FileInfo fileInfo);
    }
}

