import java.io.BufferedOutputStream;
import java.io.File;
import java.io.FileInputStream;
import java.io.IOException;
import java.io.OutputStream;
import java.net.Socket;

public class FileSender implements Runnable {
    private String mServerIpAddress;
    private int mPort;
    private FileInfo mFileInfo;
    private Socket mSocket;
    private OutputStream mOutputStream;
    private OnSendListener mOnSendListener;

    public FileSender(Context context, FileInfo fileInfo, String serverIpAddress, int port) {
        mFileInfo = fileInfo;
        mServerIpAddress = serverIpAddress;
        mPort = port;
    }

    public void setOnSendListener(OnSendListener onSendListener) {
        mOnSendListener = onSendListener;
    }

    @Override
    public void run() {
        try {
            init();
            parseHeader();
            parseBody();
            finish();
        } catch (IOException e) {
            if (mOnSendListener != null) {
                mOnSendListener.onFailure(e, mFileInfo);
            }
        }
    }

    private void init() throws IOException {
        mSocket = new Socket(mServerIpAddress, mPort);
        mOutputStream = new BufferedOutputStream(mSocket.getOutputStream());
        if (mOnSendListener != null) {
            mOnSendListener.onStart();
        }
    }

    private void parseHeader() throws IOException {
        String header = String.format("%s,%s,%s", mFileInfo.getFileName(), mFileInfo.getFileSize(), mFileInfo.getMd5());
        mOutputStream.write(header.getBytes());
        mOutputStream.flush();
    }

    private void parseBody() throws IOException {
        File file = new File(mFileInfo.getFilePath());
        FileInputStream fis = new FileInputStream(file);
        byte[] buffer = new byte[4096];
        int bytesRead;
        long totalBytesRead = 0;
        while ((bytesRead = fis.read(buffer)) != -1) {
            mOutputStream.write(buffer, 0, bytesRead);
            totalBytesRead += bytesRead;
            if (mOnSendListener != null) {
                mOnSendListener.onProgress(totalBytesRead, mFileInfo.getFileSize());
            }
        }
        fis.close();
    }

    private void finish() throws IOException {
        mOutputStream.close();
        mSocket.close();
        if (mOnSendListener != null) {
            mOnSendListener.onSuccess(mFileInfo);
        }
    }

    public interface OnSendListener {
        void onStart();
