import java.io.IOException;
import java.io.InputStream;
import java.io.OutputStream;
import java.net.Socket;

public class Client {

    public static void main(String[] args) throws IOException {
        String url = "/test";
        connect(url);
    }

    public static void connect(String url) throws IOException {
        String host = DefaultServer.HOST;
        int port = DefaultServer.PORT;
        Socket socket = new Socket(host, port);
        InputStream in = socket.getInputStream();
        OutputStream out = socket.getOutputStream();

        // Send HTTP GET request
        String request = "GET " + url + " HTTP/1.1\r\n" +
                "Host: " + host + "\r\n" +
                "Connection: Upgrade\r\n" +
                "Upgrade: websocket\r\n" +
                "Sec-WebSocket-Key: dGhlIHNhbXBsZSBub25jZQ==\r\n" +
                "Sec-WebSocket-Version: 13\r\n" +
                "\r\n";
        out.write(request.getBytes());

        // Read server response
        byte[] response = readBytes(in);
        String responseString = new String(response);
        System.out.println(responseString);

        // Verify server response
        assert responseString.contains("HTTP/1.1 101 Switching Protocols");
        assert responseString.contains("Upgrade: websocket");
        assert responseString.contains("Connection: Upgrade");

        // Send echo messages
        String echoMessage1 = "Hello, server!";
        String echoMessage2 = "How are you?";
        out.write(echoMessage1.getBytes());
        out.write(echoMessage2.getBytes());

        // Read echo responses
        byte[] echoResponse1 = readBytes(in);
        byte[] echoResponse2 = readBytes(in);
        String echoResponseString1 = new String(echoResponse1);
        String echoResponseString2 = new String(echoResponse2);
        System.out.println(echoResponseString1);
        System.out.println(echoResponseString2);

        // Verify echo responses
        assert echoResponseString1.equals(echoMessage1);
        assert echoResponseString2.equals(echoMessage2);

        // Close connection
        socket.close();
    }

    private static byte[] readBytes(InputStream in) throws IOException {
        byte[] buffer = new byte[1024];
        int bytesRead = in.read(buffer);
        byte[] result = new byte[bytesRead];
        System.arraycopy(buffer, 0, result, 0, bytesRead);
        return result;
    }
}
