import java.io.IOException;
import java.net.InetSocketAddress;
import java.nio.channels.SelectionKey;
import java.nio.channels.Selector;
import java.nio.channels.SocketChannel;
import java.util.Map;
import java.util.concurrent.ConcurrentHashMap;

public class NioTcpClient {
    private Selector nioSelector;
    private Map<String, SelectionKey> keyMap;
    private Time time;
    private SSLFactory sslFactory;
    private NetworkConfig networkConfig;

    public NioTcpClient(Time time, SSLFactory sslFactory, NetworkConfig networkConfig) throws IOException {
        this.nioSelector = Selector.open();
        this.keyMap = new ConcurrentHashMap<>();
        this.time = time;
        this.sslFactory = sslFactory;
        this.networkConfig = networkConfig;
    }

    public String connect(InetSocketAddress address, int readBufferSize, int writeBufferSize, PortType portType) throws IOException {
        SocketChannel socketChannel = SocketChannel.open();
        socketChannel.configureBlocking(false);
        socketChannel.connect(address);
        SelectionKey key = socketChannel.register(nioSelector, SelectionKey.OP_CONNECT);
        String connectionId = generateConnectionId(socketChannel);
        keyMap.put(connectionId, key);
        return connectionId;
    }

    private String generateConnectionId(SocketChannel socketChannel) {
        return socketChannel.getLocalAddress() + "-" + socketChannel.getRemoteAddress();
    }

    private Transmission createTransmission(String connectionId, SelectionKey key, String data, int timeout, PortType portType, SSLFactory.Mode mode) {
        if (mode == SSLFactory.Mode.PLAINTEXT) {
            return new PlaintextTransmission(connectionId, key, data, timeout, portType);
        } else if (mode == SSLFactory.Mode.SSL) {
            return new SSLTransmission(connectionId, key, data, timeout, portType, sslFactory);
        } else {
            throw new IllegalArgumentException("Invalid transmission mode");
        }
    }

    public void disconnect(String connectionId) {
        SelectionKey key = keyMap.get(connectionId);
        if (key != null) {
            key.cancel();
            key.channel().close();
            keyMap.remove(connectionId);
        }
    }

    public void close() throws IOException {
        for (SelectionKey key : keyMap.values()) {
            key.cancel();
            key.channel().close();
        }
        nioSelector.close();
    }
