import java.io.IOException;
import java.net.InetSocketAddress;
import java.nio.ByteBuffer;
import java.nio.channels.SelectionKey;
import java.nio.channels.Selector;
import java.nio.channels.SocketChannel;
import java.util.concurrent.atomic.AtomicReference;

public class DefaultHAClient {
    private static final int READ_MAX_BUFFER_SIZE = 4 * 1024 * 1024;
    private AtomicReference<String> masterHaAddress;
    private ByteBuffer reportOffset;
    private SocketChannel socketChannel;
    private Selector selector;
    private long lastReadTimestamp;
    private long lastWriteTimestamp;
    private long currentReportedOffset;
    private int dispatchPosition;
    private ByteBuffer byteBufferRead;
    private ByteBuffer byteBufferBackup;
    private DefaultMessageStore defaultMessageStore;

    public DefaultHAClient(DefaultMessageStore defaultMessageStore) {
        this.defaultMessageStore = defaultMessageStore;
        this.selector = null;
        this.masterHaAddress = new AtomicReference<>();
        this.reportOffset = ByteBuffer.allocate(8);
        this.socketChannel = null;
        this.lastReadTimestamp = 0;
        this.lastWriteTimestamp = 0;
        this.currentReportedOffset = 0;
        this.dispatchPosition = 0;
        this.byteBufferRead = ByteBuffer.allocate(READ_MAX_BUFFER_SIZE);
        this.byteBufferBackup = ByteBuffer.allocate(READ_MAX_BUFFER_SIZE);
    }

    public void updateHaMasterAddress(String newAddr) {
        this.masterHaAddress.set(newAddr);
    }

    public boolean reportSlaveMaxOffset(long maxOffset) {
        this.reportOffset.clear();
        this.reportOffset.putLong(maxOffset);
        this.reportOffset.flip();
        try {
            while (this.reportOffset.hasRemaining()) {
                this.socketChannel.write(this.reportOffset);
            }
            this.lastWriteTimestamp = System.currentTimeMillis();
            return true;
        } catch (IOException e) {
            return false;
        }
    }

    private void reallocateByteBuffer() {
        this.byteBufferBackup.clear();
        this.byteBufferBackup.put(this.byteBufferRead.array(), 0, this.dispatchPosition);
        this.byteBufferBackup.flip();
        this.byteBufferRead = this.byteBufferBackup;
        this.byteBufferBackup = ByteBuffer.allocate(READ_MAX_BUFFER_SIZE);
        this.dispatchPosition = 0;
    }

    private void swapByteBuffer() {
        ByteBuffer temp = this.byteBufferRead;
        this.byteBufferRead = this.byteBufferBackup;
        this.byteBufferBackup = temp;
    }

    private boolean processReadEvent() {
        try {
            int numRead = this.socketChannel.read(this.byteBufferRead);
            if (numRead > 0) {
                this.lastReadTimestamp = System.currentTimeMillis();
                this.dispatchPosition += numRead;
                if (this.dispatchPosition >= READ_MAX_BUFFER_SIZE) {
                    this.reallocateByteBuffer();
                }
                return true;
            } else if (numRead == -1) {
                this.closeMaster();
                return false;
            }
        } catch (IOException e) {
            return false;
        }
        return false;
    }

    private boolean connectMaster() {
        try {
            this.socketChannel = SocketChannel.open();
            this.socketChannel.configureBlocking(false);
            this.socketChannel.connect(new InetSocketAddress(this.masterHaAddress.get()));
            this.selector = Selector.open();
            this.socketChannel.register(this.selector, SelectionKey.OP_CONNECT);
            while (true) {
                this.selector.select();
                for (SelectionKey key : this.selector.selectedKeys()) {
                    if (key.isConnectable()) {
                        if (this.socketChannel.finishConnect()) {
                            key.interestOps(SelectionKey.OP_READ);
                            return true;
                        } else {
                            return false;
                        }
                    }
                }
                this.selector.selectedKeys().clear();
            }
        } catch (IOException e) {
            return false;
        }
    }

    private void closeMaster() {
        try {
            if (this.socketChannel != null) {
                this.socketChannel.close();
            }
            if (this.selector != null) {
                this.selector.close();
            }
        } catch (IOException e) {
            // Ignore
        }
    }

    public void shutdown() {
        this.closeMaster();
    }
}
