import java.io.IOException;
import java.net.InetAddress;
import java.net.InetSocketAddress;
import java.nio.ByteBuffer;
import java.nio.channels.*;
import java.util.concurrent.ExecutorService;
import java.util.concurrent.Executors;
import java.util.logging.Level;
import java.util.logging.Logger;

public class NioTransport {
    private ServerSocketChannel srv_channel;
    private SocketChannel client_channel;
    private Receiver receiver_thread;
    private RtReceiver receiver;
    private InetAddress host;
    private int port = 7800;
    private boolean server = false;
    private boolean direct_buffers = false;
    private Log log = new Log();

    public void options(String... options) {
        for (String option : options) {
            switch (option) {
                case "server":
                    server = true;
                    break;
                case "client":
                    server = false;
                    break;
                case "direct":
                    direct_buffers = true;
                    break;
                default:
                    if (option.contains(":")) {
                        String[] parts = option.split(":");
                        if (parts[0].equals("host")) {
                            try {
                                host = InetAddress.getByName(parts[1]);
                            } catch (Exception e) {
                                log.log(Level.SEVERE, "Invalid host: " + parts[1]);
                            }
                        } else if (parts[0].equals("port")) {
                            try {
                                port = Integer.parseInt(parts[1]);
                            } catch (NumberFormatException e) {
                                log.log(Level.SEVERE, "Invalid port: " + parts[1]);
                            }
                        }
                    }
            }
        }
    }

    public void receiver(RtReceiver receiver) {
        this.receiver = receiver;
    }

    public void start(String... options) {
        options(options);
        try {
            if (server) {
                srv_channel = ServerSocketChannel.open();
                srv_channel.socket().bind(new InetSocketAddress(host, port));
                srv_channel.configureBlocking(false);
                log.log(Level.INFO, "Server started on " + host + ":" + port);
            } else {
                client_channel = SocketChannel.open();
                client_channel.configureBlocking(false);
                client_channel.connect(new InetSocketAddress(host, port));
                log.log(Level.INFO, "Client connected to " + host + ":" + port);
            }
            receiver_thread = new Receiver();
            receiver_thread.start();
        } catch (IOException e) {
            log.log(Level.SEVERE, "Failed to start transport: " + e.getMessage());
        }
    }

    public void stop() {
        try {
            if (server) {
                srv_channel.close();
            } else {
                client_channel.close();
            }
            receiver_thread.stop();
        } catch (IOException e) {
            log.log(Level.SEVERE, "Failed to stop transport: " + e.getMessage());
        }
    }

    public void send(Object dest, byte[] buf, int offset, int length) {
        try {
            if (server) {
                srv_channel.accept().write(ByteBuffer.wrap(buf, offset, length));
            } else {
                client_channel.write(ByteBuffer.wrap(buf, offset, length));
            }
        } catch (IOException e) {
            log.log(Level.SEVERE, "Failed to send data: " + e.getMessage());
        }
    }

    private class Receiver extends Thread {
        private boolean running = true;

        public void run() {
            Selector selector;
            try {
                selector = Selector.open();
                if (server) {
                    srv_channel.register(selector, SelectionKey.OP_ACCEPT);
                } else {
                    client_channel.register(selector, SelectionKey.OP_CONNECT);
                }
                while (running) {
                    selector.select();
                    for (SelectionKey key : selector.selectedKeys()) {
                        if (key.isAcceptable()) {
                            SocketChannel client = srv_channel.accept();
                            client.configureBlocking(false);
                            client.register(selector, SelectionKey.OP_READ);
                        } else if (key.isConnectable()) {
                            client_channel.finishConnect();
                            client_channel.register(selector, SelectionKey.OP_READ);
                        } else if (key.isReadable()) {
                            SocketChannel client = (SocketChannel) key.channel();
                            ByteBuffer buffer = direct_buffers ? ByteBuffer.allocateDirect(1024) : ByteBuffer.allocate(1024);
                            int bytesRead = client.read(buffer);
                            if (bytesRead > 0) {
                                byte[] data = new byte[bytesRead];
                                buffer.flip();
                                buffer.get(data);
                                receiver.receive(data);
                            }
                        }
                    }
                    selector.selectedKeys().clear();
                }
            } catch (IOException e) {
                log.log(Level.SEVERE, "Receiver error: " + e.getMessage());
            }
        }

        public void stop() {
            running = false;
        }
    }
}
