public class ClientController {
    private BlockingQueue<Packet> inner;
    private Object lock;
    private InetSocketAddress remote;
    private Thread thread;
    private SocketChannel channel;

    public ClientController(String host, int port) {
        inner = new LinkedBlockingQueue<>();
        lock = new Object();
        remote = new InetSocketAddress(host, port);
        thread = new Thread(this::run);
    }

    public void start() {
        if (!thread.isAlive()) {
            thread.start();
        }
    }

    public boolean put(Packet packet) {
        return inner.offer(packet);
    }

    public void clear() {
        inner.clear();
    }

    private void run() {
        try {
            channel = SocketChannel.open();
            channel.configureBlocking(false);
            channel.connect(remote);
            while (!Thread.currentThread().isInterrupted()) {
                if (channel.finishConnect()) {
                    write();
                }
            }
        } catch (IOException e) {
            e.printStackTrace();
        } finally {
            try {
                channel.close();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }

    private void write() {
        Packet packet;
        while ((packet = inner.poll()) != null) {
            try {
                ByteBuffer buffer = ByteBuffer.allocate(Packet.HEADER_SIZE + packet.getData().length);
                buffer.put(packet.getChecksum());
                buffer.put(packet.getData());
                buffer.flip();
                channel.write(buffer);
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }
