import org.apache.kafka.clients.admin.Admin;
import org.apache.kafka.clients.admin.AdminClientConfig;
import org.apache.kafka.clients.admin.DescribeTopicsResult;
import org.apache.kafka.clients.admin.TopicDescription;
import org.apache.kafka.common.KafkaFuture;
import org.apache.kafka.common.KafkaException;
import org.apache.kafka.common.errors.TimeoutException;

import java.util.Collections;
import java.util.Map;
import java.util.concurrent.ExecutionException;
import java.util.concurrent.TimeUnit;
import java.util.concurrent.TimeoutException;

public class KafkaTopicAdmin implements AutoCloseable {

    private final Map<String, Object> configs;
    private final int operationTimeout;

    public KafkaTopicAdmin(Map<String, Object> configs, int operationTimeout) {
        this.configs = configs;
        this.operationTimeout = operationTimeout;
    }

    public Map<String, TopicDescription> describeTopics(String... topicNames) {
        try (Admin admin = createAdmin()) {
            DescribeTopicsResult result = admin.describeTopics(Collections.singletonMap("topic", topicNames));
            Map<String, KafkaFuture<TopicDescription>> futureMap = result.values();
            Map<String, TopicDescription> topicDescriptions = Collections.emptyMap();
            for (Map.Entry<String, KafkaFuture<TopicDescription>> entry : futureMap.entrySet()) {
                try {
                    topicDescriptions = Collections.singletonMap(entry.getKey(), entry.getValue().get(operationTimeout, TimeUnit.SECONDS));
                } catch (InterruptedException e) {
                    Thread.currentThread().interrupt();
                    throw new KafkaException("Interrupted while waiting for topic description", e);
                } catch (ExecutionException e) {
                    throw new KafkaException("Failed to describe topic", e.getCause());
                } catch (TimeoutException e) {
                    throw new KafkaException("Timed out waiting for topic description", e);
                }
            }
            return topicDescriptions;
        }
    }

    protected Admin createAdmin() {
        return Admin.create(getAdminConfig());
    }

    protected Map<String, Object> getAdminConfig() {
        return Collections.unmodifiableMap(configs);
    }

    @Override
    public void close() {
        // No-op as AdminClient is closed in describeTopics method
    }
}
