import org.java_websocket.WebSocket;
import org.java_websocket.handshake.ClientHandshake;
import org.java_websocket.server.WebSocketServer;

import java.net.InetSocketAddress;
import java.nio.ByteBuffer;

public class ChatServer extends WebSocketServer {

    public ChatServer(int port) {
        super(new InetSocketAddress(port));
    }

    public ChatServer(InetSocketAddress address) {
        super(address);
    }

    public ChatServer(int port, Draft_6455 draft) {
        super(new InetSocketAddress(port), draft);
    }

    @Override
    public void onOpen(WebSocket conn, ClientHandshake handshake) {
        // Send welcome message to new client
        conn.send("Welcome to the chat server!");

        // Notify other clients of new connection
        broadcast(conn + " joined the chat.");
    }

    @Override
    public void onClose(WebSocket conn, int code, String reason, boolean remote) {
        // Notify other clients of disconnection
        broadcast(conn + " left the chat.");
    }

    @Override
    public void onMessage(WebSocket conn, String message) {
        // Broadcast incoming text message to all clients
        broadcast(message);
    }

    @Override
    public void onMessage(WebSocket conn, ByteBuffer message) {
        // Broadcast incoming binary message to all clients
        broadcast(message);
    }

    @Override
    public void onError(WebSocket conn, Exception ex) {
        // Handle errors that occur during WebSocket operations
        ex.printStackTrace();
    }

    @Override
    public void onStart() {
        // Initialize server settings when the server starts
        setConnectionLostTimeout(0);
    }

    private void broadcast(String message) {
        // Broadcast text message to all connected clients
        for (WebSocket conn : connections()) {
            conn.send(message);
        }
    }

    private void broadcast(ByteBuffer message) {
        // Broadcast binary message to all connected clients
        for (WebSocket conn : connections()) {
            conn.send(message);
        }
    }
}
