        for (Session peer : peers.keySet()) {
            if (!peer.equals(session)) {
                try {
                    peer.getBasicRemote().sendObject(figure);
                } catch (IOException | EncodeException e) {
                    LOGGER.warning("Failed to send figure to peer: " + peer.getId());
                }
            }
        }
    }

    public void broadcastSnapshot(ByteBuffer data, Session session) {
        for (Session peer : peers.keySet()) {
            if (!peer.equals(session)) {
                try {
                    peer.getBasicRemote().sendBinary(data);
                } catch (IOException e) {
                    LOGGER.warning("Failed to send snapshot to peer: " + peer.getId());
                }
            }
        }
