import java.io.IOException;
import java.util.concurrent.ConcurrentHashMap;

@ServerEndpoint("/websocket/{name}")
public class WebSocketServerEndpoint {

    private Session session;
    private String name;
    private static ConcurrentHashMap<String, WebSocketServerEndpoint> webSocketSet = new ConcurrentHashMap<>();

    @OnOpen
    public void onOpen(Session session, @PathParam(value = "name") String name) {
        this.session = session;
        this.name = name;
        webSocketSet.put(name, this);
        GroupSending(name + " has joined the chat.");
    }

    @OnClose
    public void onClose() {
        webSocketSet.remove(name);
        GroupSending(name + " has left the chat.");
    }

    @OnMessage
    public void onMessage(String messageStr) {
        if (messageStr.startsWith("@")) {
            String[] parts = messageStr.split(" ", 2);
            AppointSending(parts[0].substring(1), parts[1]);
        } else {
            GroupSending(name + ": " + messageStr);
        }
    }

    @OnError
    public void onError(Session session, Throwable error) {
        error.printStackTrace();
    }

    public void GroupSending(String message) {
        webSocketSet.forEach((name, endpoint) -> {
            synchronized (endpoint) {
                try {
                    endpoint.session.getBasicRemote().sendText(message);
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
        });
    }

    public void AppointSending(String name, String message) {
        WebSocketServerEndpoint endpoint = webSocketSet.get(name);
        if (endpoint != null) {
            synchronized (endpoint) {
                try {
                    endpoint.session.getBasicRemote().sendText(name + ": " + message);
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
        }
    }
}
