@ServerEndpoint("/chat/{username}")
public class ChatRoomServerEndpoint {

    private static final Logger logger = Logger.getLogger(ChatRoomServerEndpoint.class.getName());
    private static final Map<String, Session> ONLINE_USER_SESSIONS = new ConcurrentHashMap<>();

    @OnOpen
    public void openSession(@PathParam("username") String username, Session session) {
        ONLINE_USER_SESSIONS.put(username, session);
        broadcastMessage("Server", username + " has joined the chat room.");
    }

    @OnMessage
    public void onMessage(@PathParam("username") String username, String message) {
        broadcastMessage(username, message);
    }

    @OnClose
    public void onClose(@PathParam("username") String username, Session session) {
        ONLINE_USER_SESSIONS.remove(username);
        broadcastMessage("Server", username + " has left the chat room.");
    }

    @OnError
    public void onError(Session session, Throwable throwable) {
        logger.severe("Error occurred: " + throwable.getMessage());
        try {
            session.close();
        } catch (IOException e) {
            logger.severe("Failed to close session: " + e.getMessage());
        }
    }

    private void broadcastMessage(String username, String message) {
        ONLINE_USER_SESSIONS.forEach((user, session) -> {
            try {
                session.getBasicRemote().sendText(username + ": " + message);
            } catch (IOException e) {
                logger.severe("Failed to send message to " + user + ": " + e.getMessage());
            }
        });
    }
}
