import io.grpc.Server;
import io.grpc.ServerBuilder;
import io.grpc.stub.StreamObserver;
import io.prometheus.client.Counter;
import io.prometheus.client.exporter.HTTPServer;
import org.apache.logging.log4j.LogManager;
import org.apache.logging.log4j.Logger;

public class DataHandler {
    private static final Logger LOG = LogManager.getLogger(DataHandler.class);
    private static final int PORT = 1111;
    private final Counter syncRequests = Counter.build()
            .name("sync_requests_total")
            .help("Total number of synchronous requests.")
            .register();
    private final Counter streamingRequests = Counter.build()
            .name("streaming_requests_total")
            .help("Total number of streaming requests.")
            .register();
    private Server grpcServer;
    private HTTPServer prometheusHttpServer;

    public void start() throws IOException {
        grpcServer = ServerBuilder.forPort(PORT)
                .addService(new DataImpl())
                .intercept(new PrometheusInterceptor(syncRequests, streamingRequests))
                .build();
        grpcServer.start();
        LOG.info("Server started, listening on " + PORT);
        prometheusHttpServer = new HTTPServer(9090);
        LOG.info("Prometheus metrics exposed on http://localhost:9090");
    }

    public void blockUntilShutdown() throws InterruptedException {
        if (grpcServer != null) {
            grpcServer.awaitTermination();
        }
    }

    public void stop() {
        if (grpcServer != null) {
            grpcServer.shutdown();
        }
        if (prometheusHttpServer != null) {
            prometheusHttpServer.stop();
        }
    }

    private static class DataImpl extends DataServiceGrpc.DataServiceImplBase {
        @Override
        public void get(DataRequest request, StreamObserver<DataResponse> responseObserver) {
            syncRequests.inc();
            String input = request.getData();
            String output = input.toUpperCase();
            responseObserver.onNext(DataResponse.newBuilder().setData(output).build());
            responseObserver.onCompleted();
        }

        @Override
        public void streamingGet(EmptyRequest request, StreamObserver<DataResponse> responseObserver) {
            streamingRequests.inc();
            for (int i = 1; i <= 10; i++) {
                responseObserver.onNext(DataResponse.newBuilder().setData("Response " + i).build());
            }
            responseObserver.onCompleted();
        }

        @Override
        public StreamObserver<DataRequest> streamingPut(StreamObserver<DataResponse> responseObserver) {
            return new StreamObserver<DataRequest>() {
                @Override
                public void onNext(DataRequest request) {
                    String input = request.getData();
                    String output = input.toUpperCase();
                    responseObserver.onNext(DataResponse.newBuilder().setData(output).build());
                }

                @Override
                public void onError(Throwable t) {
                    responseObserver.onError(t);
                }

                @Override
                public void onCompleted() {
                    responseObserver.onCompleted();
                }
            };
        }
    }
}
