public class TCPServer {
    private boolean mIsServiceDestoryed = false;
    private String[] mDefinedMessages = {"Hello", "Hi", "How are you?", "What's your name?", "Bye"};

    public void startServer() {
        new Thread(new TcpServer()).start();
    }

    public void stopServer() {
        mIsServiceDestoryed = true;
    }

    private class TcpServer implements Runnable {
        public void run() {
            ServerSocket serverSocket = null;
            try {
                serverSocket = new ServerSocket(8888);
                System.out.println("Server started on port 8888");
                while (!mIsServiceDestoryed) {
                    Socket client = serverSocket.accept();
                    new Thread(() -> responseClient(client)).start();
                }
            } catch (IOException e) {
                e.printStackTrace();
            } finally {
                try {
                    if (serverSocket != null) {
                        serverSocket.close();
                    }
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
        }
    }

    private void responseClient(Socket client) {
        try {
            BufferedReader reader = new BufferedReader(new InputStreamReader(client.getInputStream()));
            PrintWriter writer = new PrintWriter(client.getOutputStream(), true);
            String message;
            while ((message = reader.readLine()) != null) {
                System.out.println("Received message from client: " + message);
                String response = mDefinedMessages[new Random().nextInt(mDefinedMessages.length)];
                writer.println(response);
                System.out.println("Sent response to client: " + response);
            }
            reader.close();
            writer.close();
            client.close();
        } catch (IOException e) {
            e.printStackTrace();
        }
    }
}
