import java.io.IOException;
import java.io.InputStream;
import java.net.HttpURLConnection;
import java.net.URL;

import org.bouncycastle.asn1.ocsp.OCSPObjectIdentifiers;
import org.bouncycastle.asn1.ocsp.OCSPResponse;
import org.bouncycastle.cert.ocsp.OCSPException;
import org.bouncycastle.cert.ocsp.OCSPResp;

public class OCSPRequestSender {

    public OCSPResp sendOCSPRequest(String url, OCSPReq ocspReq) throws IOException {
        HttpURLConnection connection = null;
        InputStream responseStream = null;
        try {
            connection = (HttpURLConnection) new URL(url).openConnection();
            connection.setRequestMethod("POST");
            connection.setRequestProperty("Content-Type", "application/ocsp-request");
            connection.setDoOutput(true);
            ocspReq.getEncoded().writeTo(connection.getOutputStream());
            connection.connect();
            responseStream = connection.getInputStream();
            byte[] responseData = new byte[connection.getContentLength()];
            responseStream.read(responseData);
            return new OCSPResp(OCSPResponse.getInstance(responseData));
        } catch (OCSPException e) {
            throw new IOException("Failed to parse OCSP response", e);
        } finally {
            if (responseStream != null) {
                responseStream.close();
            }
            if (connection != null) {
                connection.disconnect();
            }
        }
    }
}
