import java.io.IOException;
import java.io.InputStream;
import java.io.OutputStream;
import java.net.InetAddress;
import java.net.ServerSocket;
import java.net.Socket;
import java.util.concurrent.atomic.AtomicBoolean;

public class TcpTransport {
    private ServerSocket srv_sock;
    private Socket sock;
    private OutputStream output;
    private InputStream input;
    private Receiver receiver_thread;
    private RtReceiver receiver;
    private InetAddress host;
    private int port = 7800;
    private int out_buf_size = 8192;
    private int in_buf_size = 8192;
    private boolean server = false;
    private boolean tcp_nodelay = false;
    private Log log;

    public void start(String... options) {
        for (String option : options) {
            if (option.equals("server")) {
                server = true;
            } else if (option.equals("nodelay")) {
                tcp_nodelay = true;
            } else if (option.startsWith("port=")) {
                port = Integer.parseInt(option.substring(5));
            } else if (option.startsWith("out_buf=")) {
                out_buf_size = Integer.parseInt(option.substring(8));
            } else if (option.startsWith("in_buf=")) {
                in_buf_size = Integer.parseInt(option.substring(7));
            }
        }
        try {
            if (server) {
                srv_sock = new ServerSocket(port);
                sock = srv_sock.accept();
            } else {
                host = InetAddress.getByName(options[0]);
                sock = new Socket(host, port);
            }
            sock.setTcpNoDelay(tcp_nodelay);
            output = sock.getOutputStream();
            input = sock.getInputStream();
            receiver = new RtReceiver();
            receiver_thread = new Receiver(input);
            receiver_thread.start();
        } catch (IOException e) {
            log.error("Failed to start TCP transport: " + e.getMessage());
        }
    }

    public void stop() {
        try {
            receiver_thread.stop();
            input.close();
            output.close();
            sock.close();
            if (server) {
                srv_sock.close();
            }
        } catch (IOException e) {
            log.error("Failed to stop TCP transport: " + e.getMessage());
        }
    }

    public void send(byte[] buf, int offset, int length) {
        try {
            output.write(buf, offset, length);
        } catch (IOException e) {
            log.error("Failed to send data: " + e.getMessage());
        }
    }

    private class Receiver extends Thread {
        private InputStream in;
        private AtomicBoolean running = new AtomicBoolean(true);

        public Receiver(InputStream in) {
            this.in = in;
        }

        public void run() {
            byte[] buf = new byte[in_buf_size];
            while (running.get()) {
                try {
                    int len = in.read(buf);
                    if (len > 0) {
                        receiver.onReceive(buf, len);
                    }
                } catch (IOException e) {
                    log.error("Failed to receive data: " + e.getMessage());
                    running.set(false);
                }
            }
        }

        public void stop() {
            running.set(false);
        }
    }
}
