        return wasUsedForClientToServer;
    }

    public void shutdown() {
        thread.shutdown();
    }

    private class ThreadProxy extends Thread {
        public void run() {
            try {
                while (true) {
                    sClient = socket.accept();
                    new Thread(new ClientHandler(sClient)).start();
                }
            } catch (IOException e) {
                e.printStackTrace();
            }
        }

        public void shutdown() {
            try {
                socket.close();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }

    private class ClientHandler implements Runnable {
        private Socket sClient;
        private Socket sServer;
        private InputStream isClient;
        private OutputStream osClient;
        private InputStream isServer;
        private OutputStream osServer;

        public ClientHandler(Socket sClient) {
            this.sClient = sClient;
        }

        public void run() {
            try {
                sServer = new Socket(SERVER_URL, SERVER_PORT);
                isClient = sClient.getInputStream();
                osClient = sClient.getOutputStream();
                isServer = sServer.getInputStream();
                osServer = sServer.getOutputStream();

                byte[] buffer = new byte[4096];
                int bytesRead;

                while ((bytesRead = isClient.read(buffer)) != -1) {
                    osServer.write(buffer, 0, bytesRead);
