import java.io.*;
import java.net.*;
import java.util.*;
import org.ietf.jgss.*;

public class GSSServer {
    private ServerSocket s;

    public static void main(String[] args) {
        String serviceName = "gssServer";
        int port = 12345;
        byte[] oid = null;

        for (int i = 0; i < args.length; i++) {
            if (args[i].equals("-port")) {
                port = Integer.parseInt(args[++i]);
            } else if (args[i].equals("-mech")) {
                oid = args[++i].getBytes();
            } else {
                serviceName = args[i];
            }
        }

        GSSServer server = new GSSServer();
        server.start(serviceName, port, oid);
    }

    public void start(String serviceName, int port, byte[] oid) {
        try {
            s = new ServerSocket(port);
            print("Server started on port " + port);

            GSSManager manager = GSSManager.getInstance();
            GSSName serverName = manager.createName(serviceName + "@" + InetAddress.getLocalHost().getCanonicalHostName(), null);
            Oid gssOid = new Oid(oid);
            GSSCredential server = manager.createCredential(serverName, 3600, gssOid, GSSCredential.INITIATE_ONLY);

            while (true) {
                Socket client = s.accept();
                print("Client connected: " + client.getInetAddress().getHostAddress());
                processClient(server, client);
            }
        } catch (Exception e) {
            e.printStackTrace();
            exit(1);
        }
    }

    public void processClient(GSSCredential server, Socket client) {
        try {
            GSSManager manager = GSSManager.getInstance();
            GSSContext aCtxt = manager.createContext(server);

            DataInputStream dis = new DataInputStream(client.getInputStream());
            DataOutputStream dos = new DataOutputStream(client.getOutputStream());

            exchangeWithPeer(aCtxt, dis, dos);

            aCtxt.dispose();
            client.close();
        } catch (Exception e) {
            e.printStackTrace();
            try {
                client.close();
            } catch (IOException e1) {
                e1.printStackTrace();
            }
        }
    }

    public void exchangeWithPeer(GSSContext aCtxt, DataInputStream dis, DataOutputStream dos) throws IOException {
        byte[] token = new byte[1024];
        int tokenLength;

        while (!aCtxt.isEstablished()) {
            tokenLength = dis.readInt();
            dis.readFully(token, 0, tokenLength);
            token = aCtxt.acceptSecContext(token, 0, tokenLength);
            dos.writeInt(token.length);
            dos.write(token);
            dos.flush();
        }

        print("Context established with client");

        byte[] response = "Hello from server".getBytes();
        dos.writeInt(response.length);
        dos.write(response);
        dos.flush();

        dis.close();
        dos.close();
    }

    public void usage() {
        print("Usage: java GSSServer [-port portNumber] [-mech mechanismOID] serviceName");
    }

    public void print(String msg) {
        System.out.println(msg);
    }

    public void exit(int status) {
        try {
            s.close();
        } catch (IOException e) {
            e.printStackTrace();
        }
        System.exit(status);
    }
}
