public class DNSConnection {
    private static final int PORT = 53;
    private static final int MAX_MSG_LEN = 65535;
    private ServerSocket listener;
    private Socket socket;
    private byte[] msgBytes;
    private int msgLen;
    private BufferedInputStream in;
    private OutputStream out;
    private byte[] lenBuf = new byte[2];

    public void listen() {
        try {
            listener = new ServerSocket(PORT);
            System.out.println("Listening on port " + PORT);
        } catch (IOException e) {
            System.err.println("Error listening on port " + PORT);
            e.printStackTrace();
        }
    }

    public void stopListening() {
        try {
            listener.close();
        } catch (IOException e) {
            System.err.println("Error closing listener");
            e.printStackTrace();
        }
    }

    public void openIncoming() {
        try {
            socket = listener.accept();
            in = new BufferedInputStream(socket.getInputStream());
            out = socket.getOutputStream();
            System.out.println("Accepted connection from " + socket.getInetAddress());
        } catch (IOException e) {
            System.err.println("Error accepting connection");
            e.printStackTrace();
        }
    }

    public void open(InetAddress server) {
        try {
            socket = new Socket(server, PORT);
            in = new BufferedInputStream(socket.getInputStream());
            out = socket.getOutputStream();
            System.out.println("Connected to " + server);
        } catch (IOException e) {
            System.err.println("Error connecting to " + server);
            e.printStackTrace();
        }
    }

    public void send(byte[] msgBytes) {
        try {
            msgLen = msgBytes.length;
            if (msgLen > MAX_MSG_LEN) {
                throw new IllegalArgumentException("Message length exceeds maximum allowed");
            }
            lenBuf[0] = (byte) (msgLen >> 8);
            lenBuf[1] = (byte) msgLen;
            out.write(lenBuf);
            out.write(msgBytes);
            out.flush();
        } catch (IOException e) {
            System.err.println("Error sending message");
            e.printStackTrace();
        }
    }

    public byte[] receive(boolean wait) {
        try {
            if (wait) {
                while (in.available() < 2) {
                    Thread.sleep(100);
                }
            }
            in.read(lenBuf);
            msgLen = ((lenBuf[0] & 0xFF) << 8) | (lenBuf[1] & 0xFF);
            if (msgLen > MAX_MSG_LEN) {
                throw new IllegalArgumentException("Message length exceeds maximum allowed");
            }
            msgBytes = new byte[msgLen];
            in.read(msgBytes);
            return msgBytes;
        } catch (IOException | InterruptedException e) {
            System.err.println("Error receiving message");
            e.printStackTrace();
        }
        return null;
    }

    public void close() {
        try {
            in.close();
            out.close();
            socket.close();
            System.out.println("Connection closed");
        } catch (IOException e) {
            System.err.println("Error closing connection");
