import java.io.IOException;
import java.io.InputStream;
import java.io.OutputStream;
import java.net.ServerSocket;
import java.net.Socket;

public class ConnectionManipulationProxyServer implements Runnable {
    private int localPort;
    private String host;
    private int remotePort;
    private Thread proxyThread;
    private Object enableLock = new Object();
    private boolean enableProxy = false;
    private boolean running = false;
    private Socket client;
    private Socket server;
    private ServerSocket serverSocket;

    public ConnectionManipulationProxyServer(String host, int remotePort, int localPort) {
        this.host = host;
        this.remotePort = remotePort;
        this.localPort = localPort;
    }

    public void startProxy() {
        synchronized (enableLock) {
            if (!enableProxy) {
                enableProxy = true;
                proxyThread = new Thread(this);
                proxyThread.start();
            }
        }
    }

    public void stopProxy() {
        synchronized (enableLock) {
            if (enableProxy) {
                enableProxy = false;
                proxyThread.interrupt();
            }
        }
    }

    public void killOpenSockets() {
        try {
            if (client != null) {
                client.close();
            }
            if (server != null) {
                server.close();
            }
            if (serverSocket != null) {
                serverSocket.close();
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    @Override
    public void run() {
        try {
            serverSocket = new ServerSocket(localPort);
            running = true;
            while (running) {
                client = serverSocket.accept();
                server = new Socket(host, remotePort);
                new Thread(new ClientToServer(client, server)).start();
                new Thread(new ServerToClient(server, client)).start();
            }
        } catch (IOException e) {
            e.printStackTrace();
        } finally {
            killOpenSockets();
        }
    }

    private class ClientToServer implements Runnable {
        private Socket client;
        private Socket server;

        public ClientToServer(Socket client, Socket server) {
            this.client = client;
            this.server = server;
        }

        @Override
        public void run() {
            try {
                InputStream clientInput = client.getInputStream();
                OutputStream serverOutput = server.getOutputStream();
                byte[] buffer = new byte[4096];
                int bytesRead;
                while ((bytesRead = clientInput.read(buffer)) != -1) {
                    serverOutput.write(buffer, 0, bytesRead);
                    serverOutput.flush();
                }
            } catch (IOException e) {
                e.printStackTrace();
            } finally {
                try {
                    client.close();
                    server.close();
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
        }
    }

    private class ServerToClient implements Runnable {
        private Socket server;
        private Socket client;

        public ServerToClient(Socket server, Socket client) {
            this.server = server;
            this.client = client;
        }

        @Override
        public void run() {
            try {
                InputStream serverInput = server.getInputStream();
                OutputStream clientOutput = client.getOutputStream();
                byte[] buffer = new byte[4096];
                int bytesRead;
                while ((bytesRead = serverInput.read(buffer)) != -1) {
                    clientOutput.write(buffer, 0, bytesRead);
