import java.util.concurrent.ExecutorService;
import java.util.concurrent.Executors;

public class SnapshotService implements Runnable {
    private int port;
    private ServerSocket serverSocket;
    private boolean closed;

    public SnapshotService(int port) {
        this.port = port;
        this.closed = false;
    }

    public void listen() {
        try {
            serverSocket = new ServerSocket(port);
            System.out.println("Server started on port " + serverSocket.getLocalPort());
            ExecutorService executor = Executors.newFixedThreadPool(10);
            while (!closed) {
                Socket socket = serverSocket.accept();
                executor.submit(() -> process(socket));
            }
            executor.shutdown();
        } catch (IOException e) {
            if (!isIgnoredException(e)) {
                e.printStackTrace();
            }
        }
    }

    private int openSocket(int port) {
        try {
            serverSocket = new ServerSocket(port);
            return serverSocket.getLocalPort();
        } catch (IOException e) {
            if (!isIgnoredException(e)) {
                e.printStackTrace();
            }
        }
        return -1;
    }

    private boolean isIgnoredException(Exception e) {
        return e instanceof SocketException && serverSocket.isClosed();
    }

    private void process(Socket socket) {
        try (BufferedReader inputReader = new BufferedReader(new InputStreamReader(socket.getInputStream()));
             BufferedWriter outputWriter = new BufferedWriter(new OutputStreamWriter(socket.getOutputStream()))) {
            String input;
            while ((input = inputReader.readLine()) != null) {
                String output = processCommand(input);
                outputMessage(outputWriter, output);
            }
        } catch (IOException e) {
            if (!isIgnoredException(e)) {
                e.printStackTrace();
            }
        }
    }

    private String processCommand(String input) {
