        return "Processed: " + input;
    }

    private void outputMessage(BufferedWriter outputWriter, String msg) {
        try {
            outputWriter.write(msg);
            outputWriter.newLine();
            outputWriter.flush();
        } catch (IOException e) {
            if (!isIgnoredException(e)) {
                e.printStackTrace();
            }
        }
    }

    public void close() {
        closed = true;
        try {
            serverSocket.close();
        } catch (IOException e) {
            if (!isIgnoredException(e)) {
                e.printStackTrace();
            }
        }
    }

    @Override
    public void run() {
        listen();
    }
}
