public class JavaHttpUtility {

    private static final int CONNECT_TIMEOUT = 10000;
    private static final int READ_TIMEOUT = 10000;

    public static Proxy getProxy() {
        return java.net.Proxy.NO_PROXY;
    }

    public static String doPost(String urlAddress, Map<String, String> param) {
        HttpURLConnection httpURLConnection = null;
        try {
            URL url = new URL(urlAddress);
            httpURLConnection = (HttpURLConnection) url.openConnection(getProxy());
            httpURLConnection.setRequestMethod("POST");
            httpURLConnection.setDoOutput(true);
            httpURLConnection.setConnectTimeout(CONNECT_TIMEOUT);
            httpURLConnection.setReadTimeout(READ_TIMEOUT);
            httpURLConnection.setRequestProperty("Accept-Encoding", "gzip");

            DataOutputStream wr = new DataOutputStream(httpURLConnection.getOutputStream());
            wr.writeBytes(getParamsString(param));
            wr.flush();
            wr.close();

            return handleResponse(httpURLConnection);
        } catch (Exception e) {
            throw new RuntimeException(e);
        } finally {
            if (httpURLConnection != null) {
                httpURLConnection.disconnect();
            }
        }
    }

    private static String handleResponse(HttpURLConnection httpURLConnection) {
        try {
            return readResult(httpURLConnection);
        } catch (Exception e) {
            return handleError(httpURLConnection);
        }
    }

    private static String readResult(HttpURLConnection urlConnection) throws Exception {
        BufferedReader in;
        if ("gzip".equals(urlConnection.getContentEncoding())) {
            in = new BufferedReader(new InputStreamReader(new GZIPInputStream(urlConnection.getInputStream())));
        } else {
            in = new BufferedReader(new InputStreamReader(urlConnection.getInputStream()));
        }
        String inputLine;
        StringBuilder content = new StringBuilder();
        while ((inputLine = in.readLine()) != null) {
            content.append(inputLine);
        }
        in.close();
        return content.toString();
    }

    private static String handleError(HttpURLConnection urlConnection) {
        try {
            BufferedReader in = new BufferedReader(new InputStreamReader(urlConnection.getErrorStream()));
            String inputLine;
            StringBuilder content = new StringBuilder();
            while ((inputLine = in.readLine()) != null) {
                content.append(inputLine);
            }
            in.close();
            throw new RuntimeException(content.toString());
        } catch (Exception e) {
            throw new RuntimeException(e);
        }
    }

    private static String getParamsString(Map<String, String> params) {
        StringBuilder result = new StringBuilder();
        for (Map.Entry<String, String> entry : params.entrySet()) {
            if (result.length() > 0) {
                result.append("&");
            }
            result.append(entry.getKey());
            result.append("=");
            result.append(entry.getValue());
        }
        return result.toString();
    }
}
