import java.io.*;
import java.net.ServerSocket;
import java.net.Socket;
import java.util.HashMap;
import java.util.Map;

public abstract class EmbedHttpServer implements Runnable {
    private int port;
    private ServerSocket serverSocket;

    public EmbedHttpServer(int port) {
        this.port = port;
    }

    public void start() {
        try {
            serverSocket = new ServerSocket(port);
            new Thread(this).start();
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    public void stop() {
        try {
            serverSocket.close();
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    @Override
    public void run() {
        while (!serverSocket.isClosed()) {
            try {
                Socket clientSocket = serverSocket.accept();
                new Thread(() -> handleRequest(clientSocket)).start();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }

    private void handleRequest(Socket clientSocket) {
        try {
            BufferedReader requestReader = new BufferedReader(new InputStreamReader(clientSocket.getInputStream()));
            BufferedWriter responseWriter = new BufferedWriter(new OutputStreamWriter(clientSocket.getOutputStream()));

            String requestLine = requestReader.readLine();
            String[] requestParts = requestLine.split(" ");
            String method = requestParts[0];
            String path = requestParts[1];

            Map<String, String> headers = new HashMap<>();
            String headerLine;
            while (!(headerLine = requestReader.readLine()).isEmpty()) {
                String[] headerParts = headerLine.split(": ");
                headers.put(headerParts[0], headerParts[1]);
            }

            Map<String, String> queries = parsePath(path);

            InputStream input = clientSocket.getInputStream();
            OutputStream response = clientSocket.getOutputStream();

            handle(method, path, headers, queries, input, response);

            responseWriter.flush();
            responseWriter.close();
            requestReader.close();
            clientSocket.close();
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    protected abstract void handle(String method, String path, Map<String, String> headers, Map<String, String> queries, InputStream input, OutputStream response);

    private Map<String, String> parsePath(String path) {
        Map<String, String> queries = new HashMap<>();
        int queryIndex = path.indexOf('?');
        if (queryIndex != -1) {
            String queryPart = path.substring(queryIndex + 1);
            String[] queryParts = queryPart.split("&");
            for (String query : queryParts) {
                String[] keyValue = query.split("=");
                queries.put(keyValue[0], keyValue[1]);
            }
        }
        return queries;
    }
}
