        while (true) {
            try {
                Socket clnt = socket.accept();
                new Thread(new Service(clnt)).start();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }

    private class Service implements Runnable {
        private Socket clnt;
        private InputStream in;
        private OutputStream out;

        public Service(Socket clnt) {
            this.clnt = clnt;
        }

        public void run() {
            try {
                in = clnt.getInputStream();
                out = clnt.getOutputStream();
                process(in, out);
            } catch (IOException e) {
                e.printStackTrace();
            } finally {
                try {
                    clnt.close();
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
        }

        private void process(InputStream in, OutputStream out) throws IOException {
            String cmd = readLine(in);
            if (cmd == null) {
                return;
            }
            doReply(in, out, cmd);
        }

        private String readLine(InputStream in) throws IOException {
            StringBuilder line = new StringBuilder();
            int c;
            while ((c = in.read()) >= 0) {
                if (c == '\r') {
                    c = in.read();
                    if (c == '\n') {
                        break;
                    }
                }
                line.append((char) c);
            }
            return line.toString();
        }

        private int skipLine(InputStream in) throws IOException {
            int c;
            while ((c = in.read()) >= 0) {
                if (c == '\r') {
                    c = in.read();
                    if (c == '\n') {
                        break;
                    }
                }
            }
            return c;
        }

        private void doReply(InputStream in, OutputStream out, String cmd) throws IOException {
            String[] tokens = cmd.split(" ");
            if (tokens.length != 3) {
                replyError(out, new BadHttpRequest(cmd));
                return;
            }
            String method = tokens[0];
            String filename = tokens[1];
            String version = tokens[2];
            if (!method.equals("GET") || !version.equals("HTTP/1.0")) {
                replyError(out, new BadHttpRequest(cmd));
                return;
            }
            if (filename.endsWith(".class")) {
                letUsersSendClassfile(out, filename, 0);
            } else {
                // TODO: handle static files
            }
        }

        private boolean letUsersSendClassfile(OutputStream out, String filename, int length) throws IOException {
            if (translator != null) {
                try {
                    CtClass clazz = classPool.get(filename.substring(1, filename.length() - 6));
                    clazz.instrument(translator);
                    byte[] bytes = clazz.toBytecode();
                    sendHeader(out, bytes.length, 0);
                    out.write(bytes);
                    out.flush();
                    return true;
                } catch (NotFoundException e) {
                    replyError(out, new BadHttpRequest(filename));
                    return false;
                } catch (CannotCompileException e) {
                    replyError(out, new BadHttpRequest(filename));
                    return false;
                }
            } else {
                replyError(out, new BadHttpRequest(filename));
                return false;
            }
        }

        private void sendHeader(OutputStream out, long dataLength, int filetype) throws IOException {
            out.write("HTTP/1.0 200 OK".getBytes());
            out.write(endofline);
            out.write("Content-Length: ".getBytes());
            out.write(Long.toString(dataLength).getBytes());
            out.write(endofline);
            out.write("Content-Type: ".getBytes());
            if (filetype == 0) {
                out.write("application/octet-stream".getBytes());
            }
            out.write(endofline);
            out.write(endofline);
            out.flush();
        }

        private void replyError(OutputStream out, BadHttpRequest e) throws IOException {
            out.write("HTTP/1.0 400 Bad Request".getBytes());
            out.write(endofline);
            out.write("Content-Length: ".getBytes());
            out.write(Integer.toString(e.getMessage().length()).getBytes());
            out.write(endofline);
            out.write("Content-Type: text/html".getBytes());
            out.write(endofline);
            out.write(endofline);
            out.write(e.getMessage().getBytes());
            out.flush();
        }
    }

    private class BadHttpRequest extends Exception {
        public BadHttpRequest(String msg) {
            super(msg);
        }
    }
}
