            try {
                Socket clientSocket = serverSocket.accept();
                BufferedReader requestReader = new BufferedReader(new InputStreamReader(clientSocket.getInputStream()));
                String requestLine = requestReader.readLine();
                String[] requestParts = requestLine.split(" ");
                String method = requestParts[0];
                String path = requestParts[1];
                HashMap<String, String> headers = new HashMap<>();
                String headerLine;
                while ((headerLine = requestReader.readLine()) != null && !headerLine.isEmpty()) {
                    String[] headerParts = headerLine.split(": ");
                    headers.put(headerParts[0], headerParts[1]);
                }
                InputStream requestBody = new BodyInputStream(clientSocket.getInputStream(), Integer.parseInt(headers.getOrDefault("Content-Length", "0")));
                OutputStream responseBody = clientSocket.getOutputStream();
                handle(method, path, headers, requestBody, responseBody);
                clientSocket.close();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }

    private static class BodyInputStream extends InputStream {
        private InputStream ins;
        private int n;

        public BodyInputStream(InputStream ins, int n) {
            this.ins = ins;
            this.n = n;
        }

        @Override
        public int available() throws IOException {
            return n;
        }

        @Override
        public int read() throws IOException {
            if (n <= 0) {
                return -1;
            }
            n--;
            return ins.read();
        }

        @Override
        public int read(byte[] b, int off, int len) throws IOException {
            if (n <= 0) {
                return -1;
            }
            if (len > n) {
                len = n;
            }
            int result = ins.read(b, off, len);
            n -= result;
            return result;
        }

        @Override
        public long skip(long n) throws IOException {
