    private Handler handler;
    private Thread receiveThread;

    public TcpClient() {
        HandlerThread tcpThread = new HandlerThread("tcpThread");
        tcpThread.start();
        handler = new Handler(tcpThread.getLooper());
        tryConnect();
    }

    private void tryConnect() {
        handler.post(() -> {
            while (true) {
                if (connect()) {
                    startReceive();
                    break;
                }
            }
        });
