        int ch2 = b[1] & 0x00FF;
        int ch3 = b[2] & 0x00FF;
        int ch4 = b[3] & 0x00FF;
        return ((ch1 << 24) + (ch2 << 16) + (ch3 << 8) + (ch4 << 0));
    }
}

